CREATE DEFINER=`root`@`%` FUNCTION `get_sub_unit_accrued_employee_count` (`id` INT) RETURNS INT(11) READS SQL DATA
    DETERMINISTIC
BEGIN
    IF id IS NULL
    THEN
      RETURN NULL;
    END IF;

    SELECT cst.lft, cst.rgt INTO @lft, @rgt
    FROM `ohrm_subunit` AS cst
    WHERE cst.id = id
    LIMIT 1;

    RETURN (
      SELECT COUNT(emp.emp_number) AS sub_unit_accrued_emp_count
      FROM `hs_hr_employee` AS emp WHERE emp.work_station IN (SELECT cst.id FROM `ohrm_subunit` AS cst WHERE cst.lft >= @lft AND cst.rgt <= @rgt)
    );
END$$

CREATE DEFINER=`root`@`%` FUNCTION `get_sub_unit_at_hierarchy_by_level` (`id` INT, `hierarchy_level` INT) RETURNS VARCHAR(512) CHARSET utf8 READS SQL DATA
    DETERMINISTIC
BEGIN

  IF id IS NULL
  THEN
    RETURN NULL;
  END IF;

    SELECT cst.lft, cst.rgt INTO @lft, @rgt
    FROM `ohrm_subunit` AS cst
    WHERE cst.id = id
    LIMIT 1;

  RETURN (
    SELECT `cst`.`name` AS sub_unit_path
    FROM `ohrm_subunit` AS cst
    WHERE cst.lft <= @lft AND cst.rgt >= @rgt AND cst.level = hierarchy_level
    );
END$$

CREATE DEFINER=`root`@`%` FUNCTION `get_sub_unit_path` (`id` INT) RETURNS VARCHAR(512) CHARSET utf8 READS SQL DATA
    DETERMINISTIC
BEGIN

  IF id IS NULL
  THEN
    RETURN NULL;
  END IF;

    SELECT cst.lft, cst.rgt INTO @lft, @rgt
    FROM `ohrm_subunit` AS cst
    WHERE cst.id = id
    LIMIT 1;
  
  RETURN (
    SELECT GROUP_CONCAT(`cst`.`name` order by `level` SEPARATOR ' / ') AS sub_unit_path
    FROM `ohrm_subunit` AS cst
    WHERE cst.lft <= @lft AND cst.rgt >= @rgt
    );
END$$

CREATE DEFINER=`root`@`%` FUNCTION `get_sub_unit_title` (`id` INT) RETURNS VARCHAR(255) CHARSET latin1 READS SQL DATA
    DETERMINISTIC
BEGIN

  IF id IS NULL
    THEN
     RETURN '';
  END IF;

  SELECT `cst`.`name`, `cst`.`level` INTO @name, @level
     FROM `ohrm_subunit` AS cst
     WHERE `cst`.`id` = id
     LIMIT 1;

  IF @level = 0
   THEN
     RETURN 'Unassigned to Subunits';
   ELSE
     RETURN CONCAT(@name, ' (Current)');
  END IF;

END$$

CREATE DEFINER=`root`@`%` FUNCTION `is_sub_unit_leaf_node` (`id` INT) RETURNS TINYINT(1) READS SQL DATA
    DETERMINISTIC
BEGIN
  SELECT cst.lft, cst.rgt INTO @lft, @rgt
    FROM `ohrm_subunit` AS cst
    WHERE cst.id = id
    LIMIT 1;

  SELECT COUNT(*) INTO  @children_count
    FROM `ohrm_subunit` AS cst
    WHERE cst.lft > @lft AND cst.rgt < @rgt
    LIMIT 1;

  RETURN (@children_count = 0);
END$$

