CREATE DEFINER=`root`@`%` FUNCTION `overlapsWithCurrentLeavePeriod` (`fromDate` DATE, `toDate` DATE, `empNumber` INT, `leaveTypeId` INT) RETURNS TINYINT(1) READS SQL DATA
    DETERMINISTIC
BEGIN

	DECLARE overlaps TINYINT(1);
  Declare periodOffset INT DEFAULT 0;

  SET overlaps = overlapsWithLeavePeriod(fromDate, toDate, empNumber, leaveTypeId, periodOffset);

	RETURN overlaps;
END$$

CREATE DEFINER=`root`@`%` FUNCTION `overlapsWithLeavePeriod` (`fromDate` DATE, `toDate` DATE, `empNumber` INT, `leaveTypeId` INT, `periodOffset` INT) RETURNS TINYINT(1) READS SQL DATA
    DETERMINISTIC
BEGIN

	DECLARE overlaps TINYINT(1);
	Declare startDate, endDate DATE;

	call getLeavePeriod(empNumber, leaveTypeId, periodOffset, startDate, endDate);

	-- check if within period
	IF (fromDate <= startDate AND toDate >= startDate) OR (fromDate <= endDate AND toDate >= endDate) OR (fromDate >= startDate AND toDate <= endDate) THEN
		SET overlaps = TRUE;
	ELSE
		SET overlaps = FALSE;
	END IF;

	RETURN overlaps;
END$$

CREATE DEFINER=`root`@`%` FUNCTION `resolve_empty` (`string` VARCHAR(10240) CHARSET UTF8) RETURNS VARCHAR(10240) CHARSET utf8 READS SQL DATA
    DETERMINISTIC
BEGIN
  RETURN IF(IFNULL(string, '') = '', '"  "', string);
END$$

CREATE DEFINER=`root`@`%` FUNCTION `validOnAsOfDateForLeavePeriod` (`fromDate` DATE, `toDate` DATE, `empNumber` INT, `leaveTypeId` INT, `periodOffset` INT) RETURNS TINYINT(1) READS SQL DATA
    DETERMINISTIC
BEGIN

	DECLARE valid TINYINT(1);
	Declare startDate, endDate, asOfDate DATE;

	call getLeavePeriod(empNumber, leaveTypeId, periodOffset, startDate, endDate);

  -- Look at start date if period before current. Look at end date if period after current
  IF periodOffset < 0 THEN
    set asOfDate = endDate;
  ELSEIF periodOffset > 0 THEN
    set asOfDate = startDate;
  ELSE
    set asOfDate = CURDATE();
  END IF;

  -- INSERT INTO debug(message) values(CONCAT('E FromDate:', CAST(fromDate AS CHAR), ',E ToDate:', CAST(toDate AS CHAR), ',LP Start:', CAST(startDate AS CHAR), ',LP End:', CAST(endDate AS CHAR), ',Offset:', CAST(periodOffset AS CHAR), ',asOfDate:', CAST(asOfDate AS CHAR)));

	-- check if within period
	IF (asOfDate BETWEEN fromDate AND toDate) THEN
		SET valid = TRUE;
	ELSE
		SET valid = FALSE;
	END IF;

	RETURN valid;
END$$

CREATE DEFINER=`root`@`%` FUNCTION `withinCurrentLeavePeriod` (`dateValue` DATE, `empNumber` INT, `leaveTypeId` INT) RETURNS TINYINT(1) READS SQL DATA
    DETERMINISTIC
BEGIN

	DECLARE withinPeriod TINYINT(1);
  Declare periodOffset INT DEFAULT 0;

	SET withinPeriod = withinLeavePeriod(dateValue, empNumber, leaveTypeId, periodOffset);

	RETURN withinPeriod;
END$$

CREATE DEFINER=`root`@`%` FUNCTION `withinLeavePeriod` (`dateValue` DATE, `empNumber` INT, `leaveTypeId` INT, `periodOffset` INT) RETURNS TINYINT(1) READS SQL DATA
    DETERMINISTIC
BEGIN

	DECLARE withinPeriod TINYINT(1);
	Declare startDate, endDate DATE;

	call getLeavePeriod(empNumber, leaveTypeId, periodOffset, startDate, endDate);

	-- check if within period
	IF dateValue >= startDate AND dateValue <= endDate THEN
		SET withinPeriod = TRUE;
	ELSE
		SET withinPeriod = FALSE;
	END IF;

	RETURN withinPeriod;
END$$

