--
-- Triggers `hs_hr_employee`
--
DELIMITER $$
CREATE TRIGGER `watch_delete_hs_hr_employee` BEFORE DELETE ON `hs_hr_employee` FOR EACH ROW BEGIN
  DECLARE new_version INT;
  DECLARE orangehrm_user_name VARCHAR(300 ) CHARSET UTF8;
  DECLARE affected_entity_name VARCHAR(300 ) CHARSET UTF8;
  SET new_version = (SELECT MAX(`version_id`) FROM `ohrm_audittrail_pim_personal_details_trail` WHERE `affected_entity_id` = OLD.employee_id) + 1;
  SET new_version = IFNULL(new_version, 1);
