              <display_name>All Earnings/Deductions</display_name>\n                        <width>200</width>\n                        <format/>\n                        <align/>\n                    </field>\n                </fields>\n            </display_group>\n        </display_groups>\n    </sub_report>\n    <sub_report type=\"sql\" name=\"subReport12\">\n        <id_field>empNumber</id_field>\n        <query>FROM hs_hr_emp_passport\n            WHERE $X{IN,hs_hr_emp_passport.emp_number,empNumber}\n            GROUP BY hs_hr_emp_passport.emp_number</query>\n        <display_groups>\n            <display_group name=\"Immigration_one\" type=\"one\" display=\"false\">\n                <fields>\n                    <field display=\"false\">\n                        <field_name>hs_hr_emp_passport.emp_number</field_name>\n                        <field_alias>empNumber</field_alias>\n                        <display_name>Emp Number</display_name>\n                        <width>2</width>\n                    </field>\n                </fields>\n            </display_group>\n            <display_group id=\"5\" name=\"Immigration\" type=\"many\" display=\"true\">\n                <group_header>Immigration</group_header>\n                <filters>\n                    <filter name=\"accessible-entity\">\n                        <properties>\n                            <property>\n                                <name>Entity</name>\n                                <value>Employee</value>\n                            </property>\n                            <property>\n                                <name>EntityIDField</name>\n                                <value>empNumber</value>\n                            </property>\n                            <property>\n                                <name>DataGroupNames</name>\n                                <value>immigration</value>\n                            </property>\n                        </properties>\n       