mat>\n                        <align/>\n                    </field>\n                    <field display=\"true\" id=\"89\">\n                        <field_name>hs_hr_emp_passport.ep_i9_review_date</field_name>\n                        <field_alias/>\n                        <display_name>Eligibility Review Date</display_name>\n                        <width>200</width>\n                        <format>date</format>\n                        <align/>\n                    </field>\n                    <field display=\"true\" id=\"90\">\n                        <field_name>hs_hr_emp_passport.ep_comments</field_name>\n                        <field_alias/>\n                        <display_name>Comments</display_name>\n                        <width>200</width>\n                        <format/>\n                        <align/>\n                    </field>\n                    <field display=\"true\" id=\"95\">\n                        <field_name>CASE hs_hr_emp_passport.ep_passport_type_flg WHEN 1 THEN \"Passport\" WHEN 2 THEN \"Visa\" END</field_name>\n                        <field_alias/>\n                        <display_name>Document Type</display_name>\n                        <width>200</width>\n                        <format/>\n                        <align/>\n                    </field>\n                    <field display=\"false\" id=\"100\">\n                        <field_name>hs_hr_emp_passport.ep_seqno</field_name>\n                        <field_alias/>\n                        <display_name>SeqNo</display_name>\n                        <width>100</width>\n                        <format/>\n                        <align/>\n                    </field>\n                </fields>\n            </display_group>\n        </display_groups>\n    </sub_report>\n    <sub_report type=\"sql\" name=\"subReport13\">\n        <id_field>empNumber</id_field>\n        <query>FROM ohrm_direct_deposit_record\n            WHERE $X{IN,ohrm_direct_deposit_record.employee_number,empNumber}\n            GROUP BY ohrm_direct_deposit_record.employee_number</query>\n        <display_groups>\n            <display_group name=\"Direct_Deposit_Details_one\" type=\"one\" display=\"false\">\n                <fields>\n                    <field display=\"false\">\n                        <field_name>ohrm_direct_deposit_record.employee_number</field_name>\n                        <field_alias>empNumber</field_alias>\n                        <display_name>Emp Number</display_name>\n                        <width>2</width>\n                    </field>\n                </fields>\n            </display_group>\n            <display_group id=\"17\" name=\"Direct_Deposit_Details\" type=\"many\" display=\"true\">\n                <group_header>Direct Deposit Details</group_header>\n                <filters>\n                    <filter name=\"accessible-entity\">\n                        <properties>\n                            <property>\n                                <name>Entity</name>\n                                <value>Employee</value>\n                            </property>\n                            <property>\n                                <name>EntityIDField</name>\n                                <value>empNumber</value>\n                            </property>\n                            <property>\n                                <name>DataGroupNames</name>\n                                <value>salary_history_details</value>\n                            </property>\n                        </properties>\n                    </filter>\n                </filters>\n                <fields>\n                    <field display=\"true\" id=\"161\">\n                        <field_name>{{ohrm_direct_deposit_record.amount}}</field_name>\n                        <field_alias/>\n                        <display_name>Direct Deposit Amount</display_name>\n                        <width>200</width>\n                        <format/>\n                        <align/>\n                    </field>\n                    <field display=\"true\" id=\"162\">\n                        <field_name>ohrm_direct_deposit_record.account_number</field_name>\n                        <field_alias/>\n                        <display_name>Account Number</display_name>\n                        <width>200</width>\n                        <format/>\n                        <align/>\n                    </field>\n                    <field display=\"true\" id=\"163\">\n                        <field_name>ohrm_direct_deposit_record.institute_name</field_name>\n                        <field_alias/>\n                        <display_name>Name of the Financial Institute</display_name>\n                        <width>200</width>\n                        <format/>\n                        <align/>\n                    </field>\n                    <field display=\"true\" id=\"164\">\n                        <field_name>ohrm_direct_deposit_record.account_type</field_name>\n                        <field_alias/>\n                        <display_name>Account Type</display_name>\n                        <width>200</width>\n                        <format>localize</format>\n                        <align/>\n                    </field>\n                    <field display=\"true\" id=\"165\">\n                        <field_name>ohrm_direct_deposit_record.branch_location</field_name>\n                        <field_alias/>\n                        <display_name>Branch Location</display_name>\n                        <width>200</width>\n                        <format/>\n                        <align/>\n                    </field>\n                    <field display=\"true\" id=\"166\">\n                        <field_name>ohrm_direct_deposit_record.routing_number</field_name>\n                        <field_alias/>\n                        <display_name>Routing Number</display_name>\n                        <width>200</width>\n                        <format/>\n                        <align/>\n                    </field>\n                </fields>\n            </display_group>\n        </display_groups>\n    </sub_report>\n    <sub_report type=\"sql\" name=\"subReport14\">\n        <id_field>empNumber</id_field>\n        <query>FROM ohrm_employee_work_week\n            INNER JOIN ohrm_day_type ON ohrm_day_type.id = ohrm_employee_work_week.day_type_id\n            WHERE $X{IN,ohrm_employee_work_week.emp_number,empNumber}\n            GROUP BY ohrm_employee_work_week.emp_number ORDER BY ohrm_employee_work_week.day_id ASC\n        </query>\n        <display_groups>\n            <display_group name=\"Work_Week_one\" type=\"one\" display=\"false\">\n                <fields>\n                    <field display=\"false\">\n                        <field_name>ohrm_employee_work_week.emp_number</field_name>\n                        <field_alias>empNumber</field_alias>\n                        <display_name>Emp Number</display_name>\n                        <width>2</width>\n                    </field>\n                </fields>\n            </display_group>\n            <display_group id=\"19\" name=\"Work_Week\" type=\"many\" display=\"true\">\n                <group_header>Work Week</group_header>\n                <filters>\n                    <filter name=\"accessible-entity\">\n                        <properties>\n                            <property>\n                                <name>Entity</name>\n                                <value>Employee</value>\n                            </property>\n                            <property>\n                                <name>EntityIDField</name>\n                                <value>empNumber</value>\n                            </property>\n                            <property>\n                                <name>DataGroupNames</name>\n                                <value>pim_employee_work_week</value>\n                            </property>\n                        </properties>\n                    </filter>\n                </filters>\n                <fields>\n                    <field display=\"true\" id=\"167\">\n                        <field_name>\n                            CASE ohrm_employee_work_week.day_id\n                            WHEN 1 THEN \'Monday\'\n                            WHEN 2 THEN \'Tuesday\'\n                            WHEN 3 THEN \'Wednesday\'\n                            WHEN 4 THEN \'Thursday\'\n                            WHEN 5 THEN \'Friday\'\n                            WHEN 6 THEN \'Saturday\'\n                            WHEN 7 THEN \'Sunday\'\n                            END\n                        </field_name>\n                        <field_alias/>\n                        <display_name>Day</display_name>\n                        <width>200</width>\n                        <format>localize</format>\n                        <align/>\n                    </field>\n                    <field display=\"true\" id=\"168\">\n                        <field_name>ohrm_employee_work_week.shift_start_time</field_name>\n                        <field_alias/>\n                        <display_name>Shift In</display_name>\n                        <width>200</width>\n                        <format/>\n                        <align/>\n                    </field>\n                    <field display=\"true\" id=\"169\">\n                        <field_name>ohrm_employee_work_week.shift_end_time</field_name>\n                        <field_alias/>\n                        <display_name>Shift Out</display_name>\n                        <width>200</width>\n                        <format>localize</format>\n                        <align/>\n                    </field>\n                    <field display=\"true\" id=\"170\">\n                        <field_name>ohrm_employee_work_week.lunch_start_time</field_name>\n                        <field_alias/>\n                        <display_name>Lunch In</display_name>\n                        <width>200</width>\n                        <format/>\n                        <align/>\n                    </field>\n                    <field display=\"true\" id=\"171\">\n                        <field_name>ohrm_employee_work_week.lunch_end_time</field_name>\n                        <field_alias/>\n                        <display_name>Lunch Out</display_name>\n                        <width>200</width>\n                        <format/>\n                        <align/>\n                    </field>\n                    <field display=\"true\" id=\"172\">\n                        <field_name>TRUNCATE(ohrm_employee_work_week.hours_per_day,3)</field_name>\n                        <field_alias/>\n                        <display_name>Duration</display_name>\n                        <width>200</width>\n                        <format/>\n                        <align/>\n                    </field>\n                    <field display=\"true\" id=\"173\">\n                        <field_name>ohrm_day_type.name</field_name>\n                        <field_alias/>\n                        <display_name>Day Type</display_name>\n                        <width>200</width>\n                        <format/>\n                        <align/>\n                    </field>\n                </fields>\n            </display_group>\n        </display_groups>\n    </sub_report>\n    <sub_report type=\"sql\" name=\"customFields\">\n        <id_field>empNumber</id_field>\n        <query>\n            from (\n            select emp_number, custom_field_id, custom_field_name, sequence_number, GROUP_CONCAT(IFNULL(value, \'---\') SEPARATOR \'CSEPARATOR\') as value\n            from (\n            select e.emp_number, cf.id as custom_field_id, cf.name as custom_field_name, cfv.sequence_number as sequence_number,\n\n            CASE cfv.value_type\n            WHEN 1 THEN cfv.string_value\n            WHEN 2 THEN cfv.text_value\n            WHEN 3 THEN cfv.number_value\n            WHEN 4 THEN cfv.date_value\n            WHEN 5 THEN cfa.file_name\n            WHEN 6 THEN cfv.string_value END as value\n\n            from hs_hr_employee e\n            left join hs_hr_emp_locations el on e.emp_number = el.emp_number\n            left join ohrm_custom_field_value cfv on cfv.emp_number = e.emp_number\n            left join ohrm_custom_field cf on cf.id = cfv.custom_field_id\n            left join ohrm_custom_field_attachment cfa on cfa.custom_field_value_id = cfv.id\n            left join ohrm_custom_field_location cfl on cfl.custom_field_id = cf.id\n            WHERE $X{IN, cfv.emp_number,empNumber} AND\n            (cf.global = 1 OR cfl.location_id = el.location_id)\n            GROUP BY custom_field_id,sequence_number\n\n            ) as a1\n            GROUP BY sequence_number)\n            as a\n            GROUP BY emp_number;\n        </query>\n        <display_groups>\n            <display_group id=\"199\" name=\"Custom Field EmpNumber\" type=\"one\" display=\"false\">\n                <group_header>Default Custom Field</group_header>\n                <fields>\n                    <field display=\"false\" id=\"199\">\n                        <field_name>emp_number</field_name>\n                        <field_alias>empNumber</field_alias>\n                        <display_name>emp_number</display_name>\n                        <width>1</width>\n                        <format/>\n                        <align/>\n                    </field>\n                </fields>\n            </display_group>\n        </display_groups>\n    </sub_report>\n    <sub_report type=\"dynamic\" processor=\"DynamicSubunitFullPathColumnGenerator\" name=\"fullSubUnitHierarchyLevelsDynamicColumns\">\n        <id_field>empNumber</id_field>\n        <query/>\n        <display_groups>\n            <display_group name=\"fullSubUnitHierarchyLevels\" type=\"one\" display=\"true\">\n                <group_header type=\"\">Full Sub Unit Hierarchy Levels</group_header>\n                <fields>\n                    <field display=\"false\">\n                        <field_name>fullSubunitPath.empNumber</field_name>\n                        <field_alias>empNumber</field_alias>\n                        <display_name>Emp Number</display_name>\n                        <width>2</width>\n                    </field>\n                    <field display=\"true\" type=\"dynamic\">\n                        <field_name>NULL</field_name>\n                        <field_alias>empFullSubUnitHierarchyLevels</field_alias>\n                        <display_name>Full Sub Unit Hierarchy Levels</display_name>\n                        <width>200</width>\n                        <format/>\n                        <align/>\n                    </field>\n                </fields>\n            </display_group>\n        </display_groups>\n    </sub_report>\n    <join>\n        <join_by sub_report=\"mainTable\" id=\"empNumber\"/>\n        <join_by sub_report=\"subReport1\" id=\"empNumber\"/>\n        <join_by sub_report=\"subReport2\" id=\"empNumber\"/>\n        <join_by sub_report=\"subReport3\" id=\"empNumber\"/>\n        <join_by sub_report=\"subReport4\" id=\"empNumber\"/>\n        <join_by sub_report=\"subReport5\" id=\"empNumber\"/>\n        <join_by sub_report=\"subReport6\" id=\"empNumber\"/>\n        <join_by sub_report=\"subReport7\" id=\"empNumber\"/>\n        <join_by sub_report=\"subReport8\" id=\"empNumber\"/>\n        <join_by sub_report=\"subReport9\" id=\"empNumber\"/>\n        <join_by sub_report=\"subReport10\" id=\"empNumber\"/>\n        <join_by sub_report=\"subReport11\" id=\"empNumber\"/>\n        <join_by sub_report=\"subReport12\" id=\"empNumber\"/>\n        <join_by sub_report=\"subReport13\" id=\"empNumber\"/>\n        <join_by sub_report=\"subReport14\" id=\"empNumber\"/>\n        <join_by sub_report=\"customFields\" id=\"empNumber\"/>\n        <join_by sub_report=\"fullSubUnitHierarchyLevelsDynamicColumns\" id=\"empNumber\"/>\n    </join>\n    <page_limit>50</page_limit>\n</report>\n', 1);
INSERT INTO `ohrm_advanced_report` (`id`, `name`, `unique_name`, `definition`, `enabled`) VALUES
(2, 'PIM Sample Report', 'pim.PIM_Sample_Report', '<?xml version=\"1.0\"?>\n<report>\n    <settings>\n        <csv>\n            <include_group_header>1</include_group_header>\n            <include_header>1</include_header>\n        </csv>\n        <mysql>\n            <group_concat_max_len>10000</group_concat_max_len>\n        </mysql>\n    </settings>\n    <preload_results>1</preload_results>\n    <filter_fields>        \n        <input_field type=\"select\" name=\"include\" label=\"Include\" required=\"true\" allow-multiple=\"false\"> \n            <options> \n                <excludeAllOption>true</excludeAllOption>\n                <choices type=\"list\"> \n                    <option value=\"1\">Current Employees Only</option> \n                    <option value=\"2\">Current and Past Employees</option> \n                    <option value=\"3\">Past Employees Only</option>\n                </choices> \n            </options> \n        <default_value>\"1\"</default_value></input_field>\n    </filter_fields>\n    <sub_report type=\"sql\" name=\"mainTable\">\n        <id_field>empNumber</id_field>\n        <seperator>|</seperator>\n        <query>FROM hs_hr_employee \n            LEFT JOIN ohrm_subunit ON (hs_hr_employee.work_station = ohrm_subunit.id)\n            LEFT JOIN ohrm_employment_status ON (hs_hr_employee.emp_status = ohrm_employment_status.id)\n            LEFT JOIN ohrm_job_title ON(hs_hr_employee.job_title_code = ohrm_job_title.id)\n            LEFT JOIN ohrm_job_category ON (hs_hr_employee.eeo_cat_code = ohrm_job_category.id)\n            LEFT JOIN ohrm_nationality ON(hs_hr_employee.nation_code = ohrm_nationality.id)\n            LEFT JOIN hs_hr_emp_locations AS emp_location ON(hs_hr_employee.emp_number = emp_location.emp_number)\n            LEFT JOIN ohrm_location ON(emp_location.location_id = ohrm_location.id)\n            LEFT JOIN hs_hr_emp_contract_extend ON (hs_hr_employee.emp_number = hs_hr_emp_contract_extend.emp_number)\n            LEFT JOIN hs_hr_country ON (hs_hr_employee.coun_code = hs_hr_country.cou_code)\n            LEFT JOIN ohrm_emp_termination ON(hs_hr_employee.termination_id = ohrm_emp_termination.id)\n            LEFT JOIN ohrm_emp_termination_reason ON(ohrm_emp_termination.reason_id = ohrm_emp_termination_reason.id)\n            LEFT JOIN ohrm_job_record ON(ohrm_job_record.employee_number = hs_hr_employee.emp_number)\n            LEFT JOIN ohrm_cost_centre ON(ohrm_job_record.cost_centre_id = ohrm_cost_centre.id AND ohrm_cost_centre.is_deleted=0)\n            WHERE $X{IN,hs_hr_employee.emp_number,employee_name}\n            AND $X{IN,hs_hr_employee.emp_status,employment_status}\n            AND $X{IN,hs_hr_employee.job_title_code,job_title}\n            AND $X{IN,hs_hr_employee.work_station,sub_unit}\n            AND $X{IN,ohrm_location.id,location}\n            AND $X{IN,hs_hr_country.cou_code,country}\n            AND $X{FROM_WIDGET,hs_hr_employee.joined_date,joined_date}\n            AND $X{IN,hs_hr_employee.emp_gender,gender}\n            AND $X{FROM_WIDGET|datediff(current_date(), hs_hr_employee.joined_date)/365|service_period}\n            AND $X{FROM_WIDGET|datediff(current_date(), hs_hr_employee.emp_birthday)/365|age_group}\n            AND IF($X{VAR,include} IS NULL, true, CASE $X{VAR,include} \n            WHEN 1 then termination_id IS NULL -- current employees only\n            WHEN 2 then TRUE -- current and past employees\n            ELSE termination_id IS NOT NULL -- past employees only\n            END)\n            AND IF($X{VAR,pay_grade,[\"\"]} IS NULL, true, hs_hr_employee.emp_number IN (\n            SELECT sr.employee_number FROM `ohrm_salary_record` sr WHERE $X{IN,sr.pay_grade_id,pay_grade}\n            )) AND IF($X{VAR,education,[\"\"]} IS NULL, true, hs_hr_employee.emp_number IN (\n            SELECT ee.emp_number FROM `ohrm_emp_education` ee WHERE $X{IN,ee.education_id,education}\n            )) AND IF($X{VAR,language,[\"\"]} IS NULL, true, hs_hr_employee.emp_nu