(11, 'Terminated Employee List', 'pim.Terminated_Employee_List', '<?xml version=\"1.0\"?>\n<report>\n    <settings>\n        <csv>\n            <include_group_header>1</include_group_header>\n            <include_header>1</include_header>\n        </csv>\n        <mysql>\n            <group_concat_max_len>10000</group_concat_max_len>\n        </mysql>\n    </settings>\n    <preload_results>1</preload_results>\n    <filter_fields>\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n        <input_field type=\"select\" name=\"include\" label=\"Include\" required=\"true\" allow-multiple=\"false\">\n            <options>\n                <excludeAllOption>true</excludeAllOption>\n                <choices type=\"list\">\n                    <option value=\"1\">Current Employees Only</option>\n                    <option value=\"2\">Current and Past Employees</option>\n                    <option value=\"3\">Past Employees Only</option>\n                </choices>\n            </options>\n            <default_value>\"3\"</default_value></input_field>\n    </filter_fields>\n    <sub_report type=\"sql\" name=\"mainTable\">\n        <id_field>empNumber</id_field>\n        <seperator>|</seperator>\n        <query>FROM hs_hr_employee\n            LEFT JOIN ohrm_subunit ON (hs_hr_employee.work_station = ohrm_subunit.id)\n            LEFT JOIN ohrm_employment_status ON (hs_hr_employee.emp_status = ohrm_employment_status.id)\n            LEFT JOIN ohrm_job_title ON(hs_hr_employee.job_title_code = ohrm_job_title.id)\n            LEFT JOIN ohrm_job_category ON (hs_hr_employee.eeo_cat_code = ohrm_job_category.id)\n            LEFT JOIN ohrm_nationality ON(hs_hr_employee.nation_code = ohrm_nationality.id)\n            LEFT JOIN hs_hr_emp_locations AS emp_location ON(hs_hr_employee.emp_number = emp_location.emp_number)\n            LEFT JOIN ohrm_location ON(emp_location.location_id = ohrm_location.id)\n            LEFT JOIN hs_hr_emp_contract_extend ON (hs_hr_employee.emp_number = hs_hr_emp_contract_extend.emp_number)\n            LEFT JOIN hs_hr_country ON (hs_hr_employee.coun_code = hs_hr_country.cou_code)\n            LEFT JOIN ohrm_emp_termination ON(hs_hr_employee.termination_id = ohrm_emp_termination.id)\n            LEFT JOIN ohrm_emp_termination_reason ON(ohrm_emp_termination.reason_id = ohrm_emp_termination_reason.id)\n            LEFT JOIN ohrm_job_record ON(ohrm_job_record.employee_number = hs_hr_employee.emp_number)\n            LEFT JOIN ohrm_employee_work_shift ON(ohrm_employee_work_shift.emp_number = hs_hr_employee.emp_number)\n            LEFT JOIN ohrm_work_shift ON(ohrm_work_shift.id = ohrm_employee_work_shift.work_shift_id)\n            WHERE $X{IN,hs_hr_employee.emp_number,employee_name}\n            AND $X{IN,hs_hr_employee.emp_status,employment_status}\n            AND $X{IN,hs_hr_employee.job_title_code,job_title}\n            AND $X{IN,hs_hr_employee.work_station,sub_unit}\n            AND $X{IN,ohrm_location.id,location}\n            AND $X{IN,ohrm_work_shift.id,workshift}\n            AND $X{IN,hs_hr_country.cou_code,country}\n            AND $X{FROM_WIDGET,hs_hr_employee.joined_date,joined_date}\n            AND $X{IN,hs_hr_employee.emp_gender,gender}\n            AND $X{FROM_WIDGET|datediff(current_date(), hs_hr_employee.joined_date)/365|service_period}\n            AND $X{FROM_WIDGET|datediff(current_date(), hs_hr_employee.emp_birthday)/365|age_group}\n            AND IF($X{VAR,include} IS NULL, true, CASE $X{VAR,include}\n            WHEN 1 then termination_id IS NULL -- current employees only\n            WHEN 2 then TRUE -- current and past employees\n            ELSE termination_id IS NOT NULL -- past employees only\n            END)\n            AND IF($X{VAR,pay_grade,[\"\"]} IS NULL, true, hs_hr_employee.emp_number IN (\n            SELECT sr.employee_number FROM `ohrm_salary_record` sr WHERE $X{IN,sr.pay_grade_id,pay_grade}\n            )) AND IF($X{VAR,education,[\"\"]} IS NULL, true, hs_hr_employee.emp_number IN (\n            SELECT ee.emp_number FROM `ohrm_emp_education` ee WHERE $X{IN,ee.education_id,education}\n            )) AND IF($X{VAR,language,[\"\"]} IS NULL, true, hs_hr_employee.emp_number IN (\n            SELECT el.emp_number FROM `hs_hr_emp_language` el WHERE $X{IN,el.lang_id,language}\n            )) AND IF($X{VAR,skill,[\"\"]} IS NULL, true, hs_hr_employee.emp_number IN (\n            SELECT es.emp_number FROM `hs_hr_emp_skill` es WHERE $X{IN,es.skill_id,skill}\n            )) AND IF($X{VAR,skill,[\"\"]} IS NULL, true, hs_hr_employee.emp_number IN (\n            SELECT es.emp_number FROM `hs_hr_emp_skill` es WHERE $X{IN,es.skill_id,skill}\n            )) AND $X{ACCESSIBLE_ENTITY_ID,hs_hr_employee.emp_number,Employee}\n            ORDER BY $Y{hs_hr_employee.emp_lastname}</query>\n        <display_groups>\n            <display_group id=\"1\" name=\"Personal\" type=\"one\" display=\"true\" show_header=\"true\">\n                <group_header>Personal</group_header><filters>\n    <filter name=\"accessible-entity\">\n        <properties>\n            <property>\n                <name>Entity</name>\n                <value>Employee</value>\n            </property>\n            <property>\n                <name>EntityIDField</name>\n                <value>empNumber</value>\n            </property>\n            <property>\n                <name>DataGroupNames</name>\n                <value>personal_information</value>\n            </property>                                                         \n        </properties>\n    </filter>\n</filters>\n                <fields>\n                    <field display=\"false\">\n                        <field_name>hs_hr_employee.emp_number</field_name>\n                        <field_alias>empNumber</field_alias>\n                        <display_name>Emp Number</display_name>\n                        <width>2</width>\n                    </field>\n                    <field display=\"true\" id=\"9\">\n                        <field_name>hs_hr_employee.employee_id</field_name>\n                        <field_alias>employeeId</field_alias>\n                        <display_name>Employee Id</display_name>\n                        <width>100</width>\n                        <format/>\n                        <align/>\n                    </field>\n                    <field display=\"true\" id=\"10\">\n                        <field_name>hs_hr_employee.emp_lastname</field_name>\n                        <field_alias>employeeLastname</field_alias>\n                        <display_name>Employee Last Name</display_name>\n                        <width>200</width>\n                        <format/>\n                        <align/>\n                    </field>\n                    <field display=\"true\" id=\"11\">\n                        <field_name>hs_hr_employee.emp_firstname</field_name>\n                        <field_alias>employeeFirstname</field_alias>\n                        <display_name>Employee First Name</display_name>\n                        <width>200</width>\n                        <format/>\n                        <align/>\n                    </field>\n                    <field display=\"true\" id=\"12\">\n                        <field_name>hs_hr_employee.emp_middle_name</field_name>\n                        <field_alias>employeeMiddlename</field_alias>\n                        <display_name>Employee Middle Name</display_name>\n                        <width>200</width>\n                        <format/>\n                        <align/>\n                    </field>\n\n\n\n\n\n\n\n\n\n\n\n\n                </fields>\n            </display_group>\n\n            <display_group id=\"6\" name=\"Job\" type=\"one\" display=\"true\" show_header=\"true\">\n                <group_header>Job</group_header><filters>\n    <filter name=\"accessible-entity\">\n        <properties>\n            <property>\n                <name>Entity</name>\n                <value>Employee</value>\n            </property>\n            <property>\n                <name>EntityIDField</name>\n                <value>empNumber</value>\n            </property>\n            <property>\n                <name>DataGroupNames</name>\n                <value>job_history_details</value>\n            </property>                                                         \n        </properties>\n    </filter>\n</filters>\n                <fields>\n                    <field display=\"true\" id=\"75\">\n                        <field_name>hs_hr_emp_contract_extend.econ_extend_start_date</field_name>\n                        <field_alias>empContStartDate</field_alias>\n                        <display_name>Contract Start Date</display_name>\n                        <width>200</width>\n                        <format>date</format>\n                        <align/>\n                    </field>\n                    <field display=\"true\" id=\"76\">\n                        <field_name>hs_hr_emp_contract_extend.econ_extend_end_date</field_name>\n                        <field_alias>empContEndDate</field_alias>\n                        <display_name>Contract End Date</display_name>\n                        <width>200</width>\n                        <format>date</format>\n                        <align/>\n                    </field>\n                    <field display=\"true\" id=\"77\">\n                        <field_name>ohrm_job_title.job_title</field_name>\n                        <field_alias>empJobTitle</field_alias>\n                        <display_name>Job Title</display_name>\n                        <width>200</width>\n                        <format/>\n                        <align/>\n                    </field>\n                    <field display=\"true\" id=\"78\">\n                        <field_name>ohrm_employment_status.name</field_name>\n                        <field_alias>empEmploymentStatus</field_alias>\n                        <display_name>Employment Status</display_name>\n                        <width>200</width>\n                        <format/>\n                        <align/>\n                    </field>\n                    <field display=\"true\" id=\"80\">\n                        <field_name>ohrm_job_category.name</field_name>\n                        <field_alias>empJobCategory</field_alias>\n                        <display_name>Job Category</display_name>\n                        <width>200</width>\n                        <format>localize</format>\n                        <align/>\n                    </field>\n                    <field display=\"true\" id=\"81\">\n                        <field_name>hs_hr_employee.joined_date</field_name>\n                        <field_alias>empJoinedDate</field_alias>\n                        <display_name>Joined Date</display_name>\n                        <width>100</width>\n                        <format>date</format>\n                        <align/>\n                    </field>\n                    <field display=\"true\" id=\"82\">\n                        <field_name>ohrm_subunit.name</field_name>\n                        <field_alias>empSubUnit</field_alias>\n                        <display_name>Sub Unit</display_name>\n                        <width>200</width>\n                        <format/>\n                        <align/>\n                    </field>\n                    <field display=\"true\" id=\"83\">\n                        <field_name>ohrm_location.name</field_name>\n                        <field_alias>empLocation</field_alias>\n                        <display_name>Location</display_name>\n                        <width>200</width>\n                        <format/>\n                        <align/>\n                    </field>\n                    <field display=\"true\" id=\"jb_01\">\n                        <field_name>ohrm_work_shift.name</field_name>\n                        <field_alias>workShift</field_alias>\n                        <display_name>Work Shift</display_name>\n                        <width>200</width>\n                        <format/>\n                        <align/>\n                    </field>\n                    <field display=\"true\" id=\"113\">\n                        <field_name>ohrm_emp_termination.termination_date</field_name>\n                        <field_alias>terminationDate</field_alias>\n                        <display_name>Termination Date</display_name>\n                        <width>100</width>\n                        <format>date</format>\n                        <align/>\n                    </field>\n                    <field display=\"true\" id=\"114\">\n                        <field_name>ohrm_emp_termination_reason.name</field_name>\n                        <field_alias>terminationReason</field_alias>\n                        <display_name>Termination Reason</display_name>\n                        <width>100</width>\n                        <format>localize</format>\n                        <align/>\n                    </field>\n                    <field display=\"true\" id=\"120\">\n                        <field_name>ohrm_emp_termination.note</field_name>\n                        <field_alias>getNote</field_alias>\n                        <display_name>Termination Note</display_name>\n                        <width>100</width>\n                        <format/>\n                        <align/>\n                    </field>\n                    <field display=\"true\" id=\"154\">\n                        <field_name>ohrm_job_record.probation_end_date</field_name>\n                        <field_alias>getProbationEndDate</field_alias>\n                        <display_name>Probation End Date</display_name>\n                        <width>200</width>\n                        <format>date</format>\n                        <align/>\n                    </field>\n                    <field display=\"true\" id=\"155\">\n                        <field_name>ohrm_job_record.date_of_permanency</field_name>\n                        <field_alias>getDateOfPermanency</field_alias>\n                        <display_name>Date of Permanency</display_name>\n                        <width>200</width>\n                        <format>date</format>\n                        <align/>\n                    </field>\n                    <field display=\"true\" id=\"156\">\n                        <field_name>ohrm_job_record.effective_date</field_name>\n                        <field_alias>getEffectiveDate</field_alias>\n                        <display_name>Effective Date</display_name>\n                        <width>200</width>\n                        <format>date</format>\n                        <align/>\n                    </field>\n                    <field display=\"true\" id=\"157\">\n                        <field_name>ohrm_job_record.comment</field_name>\n                        <field_alias>getComment</field_alias>\n                        <display_name>Comment</display_name>\n                        <width>200</width>\n                        <format/>\n                        <align/>\n                    </field>\n                </fields>\n            </display_group>\n        </display_groups>\n    </sub_report>\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n    <page_limit>100</page_limit>\n</report>', 1),
(12, 'Head Count Report', 'pim_canned.Head_Count_Report', '<?xml version=\"1.0\"?>\n<report>\n    <settings>\n        <csv>\n            <include_group_header>0</include_group_header>\n            <include_header>1</include_header>\n        </csv>\n        <mysql>\n            <group_concat_max_len>10000</group_concat_max_len>\n        </mysql>\n    </settings>\n    <preload_results>0</preload_results>\n    <filter_fields>\n        <input_field type=\"select\" name=\"employment_status\" label=\"Employment Status\" allow-multiple=\"false\">\n            <options> \n                <choices type=\"sql\"> \n                    <query>SELECT id, name FROM ohrm_employment_status ORDER BY name</query> \n                    <value>id</value> \n                    <label>name</label> \n                </choices>\n            </options>\n            <default_value></default_value>\n        </input_field>\n        <input_field type=\"sub_unit_all\" name=\"sub_unit\" label=\"Sub Unit\" required=\"false\" allow-multiple=\"false\">\n            <options> \n                <choices type=\"sql\"> \n                    <query>SELECT id, name, level FROM ohrm_subunit where id != 1 ORDER BY lft</query> \n                    <value>id</value> \n                    <label>name</label> \n                </choices>\n                <all_option_value>1</all_option_value>\n            </options>\n            <default_value>\"1\"</default_value>\n        </input_field>                \n        <input_field type=\"select\" name=\"include\" label=\"Include\" required=\"false\" allow-multiple=\"false\">\n            <options>\n                <excludeAllOption>true</excludeAllOption>\n                <choices type=\"list\">\n                    <option value=\"1\">Current Employees Only</option>\n                    <option value=\"2\">Current and Past Employees</option>\n                    <option value=\"3\">Past Employees Only</option>\n                </choices>\n            </options>\n            <default_value>\"1\"</default_value>\n        </input_field>        \n    </filter_fields>\n    <charts>\n        <chart type=\"pie\">\n            <data>emp_count</data>\n            <labels>sub_unit</labels>\n        </chart>\n    </charts>\n    <sub_report type=\"sql\" name=\"mainTable\">\n        <id_field>empNumber</id_field>\n        <seperator>|</seperator>\n        <query>FROM (\n            \nSELECT \n    CASE WHEN p.id IS NULL THEN get_sub_unit_title(s.id) ELSE p.name END AS sub_unit,\n    COUNT(emp_number) AS emp_count,CASE WHEN p.id IS NULL THEN s.id ELSE p.id END AS SubUnitId,\n    IF(e.emp_status is NULL ,0,e.emp_status) AS empStatus,\n    IF(p.id is NULL OR is_sub_unit_leaf_node(p.id), \'leaf-node\', \'inner-node\') AS NodeType\n                        \nFROM hs_hr_employee e\n\nLEFT JOIN ohrm_subunit s\nON $X{IN,s.id,sub_unit}\n\nLEFT JOIN ohrm_subunit c\nON e.work_station = c.id\n\nLEFT JOIN ohrm_subunit p\nON p.level = s.level + 1 AND s.lft &lt; p.lft and s.rgt &gt; p.rgt AND p.lft &lt;= c.lft and p.rgt &gt;= c.rgt\n\nWHERE\n\ne.deleted_at IS NULL AND\n\n$X{IN,e.emp_status,employment_status}\n\nAND\n\nIF($X{VAR,include} IS NULL, true, CASE $X{VAR,include}\n            WHEN 1 then termination_id IS NULL -- current employees only\n            WHEN 2 then TRUE -- current and past employees\n            ELSE termination_id IS NOT NULL -- past employees only\n            END)\n\nAND \n\n((s.id = 1)\nOR\n(p.id is not null or s.id = c.id))\n\nGROUP BY sub_unit\n) AS A\n\n</query>\n        <display_groups>\n            <display_group id=\"1\" name=\"Main\" type=\"one\" display=\"true\" show_header=\"false\">\n                <group_header>Main</group_header>\n                <fields>\n                    <field display=\"false\" id=\"1\">\n                        <field_name>SubUnitId</field_name>\n                        <field_alias>SubUnitId</field_alias>\n                        <display_name>Sub Unit ID</display_name>\n                        <width>2</width>\n                    </field>\n                    <field display=\"true\" id=\"2\">\n                        <field_name>sub_unit</field_name>\n                        <field_alias>sub_unit</field_alias>\n                        <display_name>Sub Unit</display_name>\n                        <width>2</width>\n                        <link type=\"reload\">\n                            <filter_by>sub_unit</filter_by>\n                            <filter_value>SubUnitId</filter_value>\n                            <if>\n                                <field>NodeType</field>\n                                <value>inner-node</value>\n                            </if>\n                        </link>\n                    </field>\n                    <field display=\"true\" id=\"3\">\n                        <field_name>emp_count</field_name>\n                        <field_alias>emp_count</field_alias>\n                        <display_name>Number of Employees</display_name>\n                        <width>100</width>\n                        <format/>\n                        <align/>\n                    </field>\n                    <field display=\"false\" id=\"4\">\n                        <field_name>empStatus</field_name>\n                        <field_alias>empStatus</field_alias>\n                        <display_name>Employee Status</display_name>\n                        <width>200</width>\n                        <format/>\n                        <align/>\n                    </field>\n                    <field display=\"false\" id=\"5\">\n                        <field_name>NodeType</field_name>\n                        <field_alias>NodeType</field_alias>\n                        <display_name>Node Type</display_name>\n                        <width>200</width>\n                        <format/>\n                        <align/>\n                    </field>                    \n                </fields>\n            </display_group>\n        </display_groups>\n    </sub_report>\n    <summarySection>\n        <summary name=\"totalSummary\">\n            <field displayField=\"sub_unit\" value=\"Total\" />\n            <field displayField=\"emp_count\" value=\"%SUM(emp_count)%\"/>\n        </summary>\n    </summarySection>    \n    <page_limit>-1</page_limit>\n</report>', 1),
