rty>\n                                <name>EntityIDField</name>\n                                <value>empNumber</value>\n                            </property>\n                            <property>\n                                <name>DataGroupNames</name>\n                                <value>supervisor</value>\n                            </property>\n                        </properties>\n                    </filter>\n                </filters>\n                <fields>\n                    <field display=\"true\" id=\"62\">\n                        <field_name>CONCAT(supervisor.emp_firstname, \' \', supervisor.emp_lastname)</field_name>\n                        <field_alias/>\n                        <display_name>Supervisors</display_name>\n                        <width>200</width>\n                        <format/>\n                        <align/>\n                    </field>\n                </fields>\n            </display_group>\n        </display_groups>\n    </sub_report>\n\n\n    <join>\n        <join_by sub_report=\"mainTable\" id=\"empNumber\"/>\n\n\n        <join_by sub_report=\"subReport8\" id=\"empNumber\"/>\n        <join_by sub_report=\"subReport9\" id=\"empNumber\"/>\n\n\n    </join>\n    <page_limit>50</page_limit>\n</report>', 1),
(16, 'Leave Entitlements and Usage Report', 'Leave_Entitlements_and_Usage_Report_By_Employee', '<report>\n    <settings>\n        <csv>\n            <include_group_header>1</include_group_header>\n            <include_header>1</include_header>\n        </csv>\n    </settings>\n    <filter_fields>\n        <input_field type=\"text\" name=\"empNumber\" label=\"Employee Number\"></input_field>\n        <input_field type=\"text\" name=\"fromDate\" label=\"From\"></input_field>\n        <input_field type=\"text\" name=\"toDate\" label=\"To\"></input_field>\n        <input_field type=\"text\" name=\"asOfDate\" label=\"AsOf\"></input_field>\n        <input_field type=\"text\" name=\"eligible_leave_types\" label=\"leave types\"></input_field>\n        <input_field type=\"text\" name=\"period_offset\" label=\"Leave Period Offset\"></input_field>\n    </filter_fields>\n\n    <sub_report type=\"sql\" name=\"mainTable\">\n        <query>FROM ohrm_leave_type\n            LEFT JOIN ohrm_operational_country o ON ohrm_leave_type.operational_country_id = o.id\n            LEFT JOIN hs_hr_country c on c.cou_code = o.country_code\n            WHERE\n            (($X{IN,ohrm_leave_type.id,eligible_leave_types}) ||\n            ((SELECT count(le.id) FROM ohrm_leave_entitlement le WHERE le.leave_type_id = ohrm_leave_type.id\n            AND $X{=,le.emp_number,empNumber} AND le.deleted = 0) > 0) ||\n            ((SELECT count(adj.id) FROM ohrm_leave_adjustment adj WHERE adj.leave_type_id = ohrm_leave_type.id\n            AND $X{=,adj.emp_number,empNumber} AND adj.deleted = 0) > 0) ||\n            ((SELECT count(ll.id) FROM ohrm_leave ll WHERE ll.leave_type_id = ohrm_leave_type.id\n            AND $X{=,ll.emp_number,empNumber} AND ll.status NOT IN (-1, 0, 4, 5)) > 0)\n            ) AND\n            ((deleted = 0) OR (SELECT count(l.id) FROM ohrm_leave l WHERE l.status = 3 AND l.leave_type_id = ohrm_leave_type.id) > 0)\n            ORDER BY ohrm_leave_type.id\n        </query>\n        <id_field>leaveTypeId</id_field>\n        <display_groups>\n            <display_group name=\"leavetype\" type=\"one\" display=\"true\">\n                <group_header></group_header>\n                <fields>\n                    <field display=\"false\">\n                        <field_name>ohrm_leave_type.id</field_name>\n                        <field_alias>leaveTypeId</field_alias>\n                        <display_name>Leave Type ID</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"false\">\n                        <field_name>ohrm_leave_type.exclude_in_reports_if_no_entitlement</field_name>\n                        <field_alias>exclude_if_no_entitlement</field_alias>\n                        <display_name>Exclude</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"false\">\n                        <field_name>ohrm_leave_type.deleted</field_name>\n                        <field_alias>leave_type_deleted</field_alias>\n                        <display_name>Leave Type Deleted</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"false\">\n                        <field_name>c.cou_name</field_name>\n                        <field_alias>country_name</field_alias>\n                        <display_name>Operational Country Name</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"true\">\n                        <field_name>ohrm_leave_type.name</field_name>\n                        <field_alias>leaveType</field_alias>\n                        <display_name>Leave Type</display_name>\n                        <width>160</width>\n                    </field>s\n                </fields>\n            </display_group>\n        </display_groups>\n    </sub_report>\n    <sub_report type=\"sql\" name=\"current_period\">\n        <query>FROM ohrm_leave_type WHERE deleted = 0 ORDER BY ohrm_leave_type.id</query>\n        <id_field>leaveTypeId</id_field>\n        <display_groups>\n            <display_group name=\"leavetype\" type=\"one\" display=\"true\">\n                <group_header></group_header>\n                <fields>\n                    <field display=\"false\">\n                        <field_name>ohrm_leave_type.id</field_name>\n                        <field_alias>leaveTypeId</field_alias>\n                        <display_name>Leave Type ID</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"false\">\n                        <field_name>ohrm_leave_type.id</field_name>\n                        <field_alias>fromDate</field_alias>\n                        <display_name>From Date</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"false\">\n                        <field_name>ohrm_leave_type.id</field_name>\n                        <field_alias>toDate</field_alias>\n                        <display_name>To Date</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"true\">\n                        <field_name>ohrm_leave_type.name</field_name>\n                        <field_alias>current</field_alias>\n                        <display_name>Leave Period</display_name>\n                        <width>160</width>\n                    </field>\n                </fields>\n            </display_group>\n        </display_groups>\n    </sub_report>\n    <sub_report type=\"sql\" name=\"entitlementsTotal\">\n        <query>\n\n            FROM (\n            SELECT ohrm_leave_entitlement.id as id,\n            ohrm_leave_entitlement.leave_type_id as leave_type_id,\n            sum(IF(validOnAsOfDateForLeavePeriod(ohrm_leave_entitlement.from_date, ohrm_leave_entitlement.to_date, $X{VAR,empNumber}, ohrm_leave_entitlement.leave_type_id, $X{VAR,period_offset}), ohrm_leave_entitlement.no_of_days, ohrm_leave_entitlement.days_used)) AS available\n\n            FROM ohrm_leave_entitlement\n            WHERE ohrm_leave_entitlement.deleted=0 AND $X{=,ohrm_leave_entitlement.emp_number,empNumber} AND\n            $X{IN,ohrm_leave_entitlement.leave_type_id,leaveTypeId} AND\n            overlapsWithLeavePeriod(ohrm_leave_entitlement.from_date, ohrm_leave_entitlement.to_date, $X{VAR,empNumber}, ohrm_leave_entitlement.leave_type_id, $X{VAR,period_offset})\n\n\n            GROUP BY ohrm_leave_entitlement.id\n            ) AS A\n\n            GROUP BY A.leave_type_id\n            ORDER BY A.leave_type_id\n\n        </query>\n        <id_field>leaveTypeId</id_field>\n        <display_groups>\n            <display_group name=\"leavetype\" type=\"one\" display=\"true\">\n                <group_header></group_header>\n                <fields>\n                    <field display=\"false\">\n                        <field_name>A.leave_type_id</field_name>\n                        <field_alias>leaveTypeId</field_alias>\n                        <display_name>Leave Type ID</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"false\">\n                        <field_name>count(id)</field_name>\n                        <field_alias>entitlement_count</field_alias>\n                        <display_name>Entitlement Count</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"true\">\n                        <field_name>sum(A.available)</field_name>\n                        <field_alias>entitlement_total</field_alias>\n                        <display_name>Entitlements (Days)</display_name>\n                        <width>85</width>\n                        <align>right</align>\n                        <link><href>leave.balance_report.view_leave_entitlement({employeeId:$P{empNumber},leaveTypeId:$P{leaveTypeId},from:\'$P{fromDate}\',to:\'$P{toDate}\',id:0})</href></link>\n                    </field>\n                </fields>\n            </display_group>\n        </display_groups>\n    </sub_report>\n\n    <sub_report type=\"sql\" name=\"pendingQuery\">\n        <query>\n            FROM ohrm_leave_type LEFT JOIN\n            ohrm_leave ON ohrm_leave_type.id = ohrm_leave.leave_type_id AND\n            $X{=,ohrm_leave.emp_number,empNumber} AND\n            ohrm_leave.status IN (SELECT s.status FROM ohrm_leave_status s WHERE s.name LIKE \'%PENDING APPROVAL%\') AND\n            withinLeavePeriod(ohrm_leave.date, $X{VAR,empNumber}, ohrm_leave.leave_type_id, $X{VAR,period_offset})\n            LEFT JOIN ohrm_leave_leave_entitlement ON ohrm_leave_leave_entitlement.leave_id = ohrm_leave.id\n\n            WHERE\n            ohrm_leave_type.deleted = 0 AND\n            $X{IN,ohrm_leave_type.id,leaveTypeId}\n\n            GROUP BY ohrm_leave_type.id\n            ORDER BY ohrm_leave_type.id\n        </query>\n        <id_field>leaveTypeId</id_field>\n        <display_groups>\n            <display_group name=\"entitlement_usage\" type=\"one\" display=\"true\" show_header=\"true\">\n                <group_header>Usage of Entitlements</group_header>\n                <fields>\n                    <field display=\"false\">\n                        <field_name>ohrm_leave_type.id</field_name>\n                        <field_alias>leaveTypeId</field_alias>\n                        <display_name>Leave Type ID</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"false\">\n                        <field_name>GROUP_CONCAT(DISTINCT ohrm_leave.leave_request_id)</field_name>\n                        <field_alias>pendingLeaveRequests</field_alias>\n                        <display_name>Pending Leave Requests</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"true\">\n                        <field_name>sum(IFNULL(ohrm_leave_leave_entitlement.length_days,0))</field_name>\n                        <field_alias>pending</field_alias>\n                        <display_name>Pending Approval (Days)</display_name>\n                        <width>67</width>\n                        <align>right</align>\n                        <link><href>leave.balance_report.view_leave_list({filter: \'$P{pendingLeaveRequests}\',showOverdrawn: 1,hideEmployeeColumn: true})</href></link>\n                    </field>\n                </fields>\n            </display_group>\n        </display_groups>\n    </sub_report>\n\n    <sub_report type=\"sql\" name=\"scheduledQuery\">\n        <query>\n            FROM ohrm_leave_type LEFT JOIN\n            ohrm_leave ON ohrm_leave_type.id = ohrm_leave.leave_type_id AND\n            $X{=,ohrm_leave.emp_number,empNumber} AND\n            ohrm_leave.status = 2 AND\n            withinLeavePeriod(ohrm_leave.date, $X{VAR,empNumber}, ohrm_leave.leave_type_id, $X{VAR,period_offset})\n            LEFT JOIN ohrm_leave_leave_entitlement ON ohrm_leave_leave_entitlement.leave_id = ohrm_leave.id\n\n            WHERE\n            ohrm_leave_type.deleted = 0 AND\n            $X{IN,ohrm_leave_type.id,leaveTypeId}\n\n            GROUP BY ohrm_leave_type.id\n            ORDER BY ohrm_leave_type.id\n        </query>\n        <id_field>leaveTypeId</id_field>\n        <display_groups>\n            <display_group name=\"entitlement_usage\" type=\"one\" display=\"true\" show_header=\"true\">\n                <group_header>Usage of Entitlements</group_header>\n                <fields>\n                    <field display=\"false\">\n                        <field_name>ohrm_leave_type.id</field_name>\n                        <field_alias>leaveTypeId</field_alias>\n                        <display_name>Leave Type ID</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"false\">\n                        <field_name>GROUP_CONCAT(DISTINCT ohrm_leave.leave_request_id)</field_name>\n                        <field_alias>scheduledLeaveRequests</field_alias>\n                        <display_name>Scheduled Leave Requests</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"true\">\n                        <field_name>sum(IFNULL(ohrm_leave_leave_entitlement.length_days,0))</field_name>\n                        <field_alias>scheduled</field_alias>\n                        <display_name>Scheduled (Days)</display_name>\n                        <width>80</width>\n                        <align>right</align>\n                        <link><href>leave.balance_report.view_leave_list({filter: \'$P{scheduledLeaveRequests}\',showOverdrawn: 1,hideEmployeeColumn: true})</href></link>\n                    </field>\n                </fields>\n            </display_group>\n        </display_groups>\n    </sub_report>\n\n    <sub_report type=\"sql\" name=\"takenQuery\">\n        <query>\n            FROM ohrm_leave\n\n            LEFT JOIN ohrm_leave_leave_entitlement ON ohrm_leave_leave_entitlement.leave_id = ohrm_leave.id\n\n            WHERE $X{=,emp_number,empNumber} AND\n            status = 3 AND\n            $X{IN,ohrm_leave.leave_type_id,leaveTypeId} AND\n            withinLeavePeriod(ohrm_leave.date, $X{VAR,empNumber}, ohrm_leave.leave_type_id, $X{VAR,period_offset})\n\n            GROUP BY leave_type_id\n            ORDER BY ohrm_leave.leave_type_id\n        </query>\n        <id_field>leaveTypeId</id_field>\n        <display_groups>\n            <display_group name=\"entitlement_usage\" type=\"one\" display=\"true\" show_header=\"true\">\n                <group_header>Usage of Entitlements</group_header>\n                <fields>\n                    <field display=\"false\">\n                        <field_name>ohrm_leave.leave_type_id</field_name>\n                        <field_alias>leaveTypeId</field_alias>\n                        <display_name>Leave Type ID</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"false\">\n                        <field_name>GROUP_CONCAT(DISTINCT ohrm_leave.leave_request_id)</field_name>\n                        <field_alias>takenLeaveRequests</field_alias>\n                        <display_name>Taken Leave Requests</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"true\">\n                        <field_name>sum(IFNULL(ohrm_leave_leave_entitlement.length_days,0))</field_name>\n                        <field_alias>taken</field_alias>\n                        <display_name>Taken (Days)</display_name>\n                        <width>60</width>\n                        <align>right</align>\n                        <link><href>leave.balance_report.view_leave_list({filter: \'$P{takenLeaveRequests}\',showOverdrawn: 1,hideEmployeeColumn: true})</href></link>\n                    </field>\n                </fields>\n            </display_group>\n        </display_groups>\n    </sub_report>\n    <sub_report type=\"sql\" name=\"unused\">\n        <query>FROM ohrm_leave_type WHERE deleted = 0 AND $X{IN,ohrm_leave_type.id,leaveTypeId} ORDER BY ohrm_leave_type.id</query>\n        <id_field>leaveTypeId</id_field>\n        <display_groups>\n            <display_group name=\"rest\" type=\"one\" display=\"true\" show_header=\"true\">\n                <group_header></group_header>\n                <fields>\n                    <field display=\"false\">\n                        <field_name>ohrm_leave_type.id</field_name>\n                        <field_alias>leaveTypeId</field_alias>\n                        <display_name>Leave Type ID</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"true\">\n                        <field_name>ohrm_leave_type.name</field_name>\n                        <field_alias>unused</field_alias>\n                        <display_name>Available Balance (Days)</display_name>\n                        <width>70</width>\n                        <align>right</align>\n                    </field>\n                </fields>\n            </display_group>\n        </display_groups>\n    </sub_report>\n    <sub_report type=\"sql\" name=\"overdrawn\">\n        <query>\n            FROM (\n            SELECT\n\n            ohrm_leave.id as leave_id,\n            ohrm_leave.leave_request_id as leave_request_id,\n            ohrm_leave.leave_type_id as leave_type_id,\n            ohrm_leave.length_days as leave_length_days,\n            sum(IFNULL(ohrm_leave_leave_entitlement.length_days,0)) AS entitlement_length_days\n\n            FROM ohrm_leave\n\n            LEFT JOIN ohrm_leave_leave_entitlement ON ohrm_leave_leave_entitlement.leave_id = ohrm_leave.id\n            WHERE $X{=,emp_number,empNumber} AND\n            $X{IN,ohrm_leave.leave_type_id,leaveTypeId} AND\n            status NOT IN(-1, 0, 4, 5) AND\n            withinLeavePeriod(ohrm_leave.date, $X{VAR,empNumber}, ohrm_leave.leave_type_id, $X{VAR,period_offset})\n\n            GROUP BY ohrm_leave.id\n\n            ) AS OD\n\n            GROUP BY leave_type_id\n            ORDER BY leave_type_id\n        </query>\n        <id_field>leaveTypeId</id_field>\n        <display_groups>\n            <display_group name=\"rest\" type=\"one\" display=\"true\">\n                <group_header></group_header>\n                <fields>\n                    <field display=\"false\">\n                        <field_name>leave_type_id</field_name>\n                        <field_alias>leaveTypeId</field_alias>\n                        <display_name>Leave Type ID</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"false\">\n                        <field_name>GROUP_CONCAT(DISTINCT IF(leave_length_days > IFNULL(entitlement_length_days,0),leave_request_id, NULL))</field_name>\n                        <field_alias>overdrawn_leave_requests</field_alias>\n                        <display_name>Overdrawn Leave Requests</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"true\">\n                        <field_name>sum(leave_length_days) - sum(IFNULL(entitlement_length_days,0))</field_name>\n                        <field_alias>overdrawn</field_alias>\n                        <display_name>Total Overdrawn (Days)</display_name>\n                        <description>Leave without entitlements</description>\n                        <width>80</width>\n                        <align>right</align>\n                        <link><href>leave.balance_report.view_leave_list({filter: \'$P{overdrawn_leave_requests}\',showOverdrawn: 1,skipFullyOverdrawn: false,hideEmployeeColumn: true})</href></link>\n                    </field>\n                </fields>\n            </display_group>\n        </display_groups>\n    </sub_report>\n\n    <sub_report type=\"sql\" name=\"adjustments\">\n        <query>\n            FROM ohrm_leave_adjustment\n\n            WHERE ohrm_leave_adjustment.deleted=0 AND $X{=,ohrm_leave_adjustment.emp_number,empNumber} AND\n            $X{IN,ohrm_leave_adjustment.leave_type_id,leaveTypeId} AND\n            overlapsWithLeavePeriod(ohrm_leave_adjustment.from_date, ohrm_leave_adjustment.to_date, $X{VAR,empNumber}, ohrm_leave_adjustment.leave_type_id, $X{VAR,period_offset})\n\n            GROUP BY ohrm_leave_adjustment.leave_type_id\n            ORDER BY ohrm_leave_adjustment.leave_type_id\n        </query>\n        <id_field>leaveTypeId</id_field>\n        <display_groups>\n            <display_group name=\"rest\" type=\"one\" display=\"true\">\n                <group_header></group_header>\n                <fields>\n                    <field display=\"false\">\n                        <field_name>ohrm_leave_adjustment.leave_type_id</field_name>\n                        <field_alias>leaveTypeId</field_alias>\n                        <display_name>Leave Type ID</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"false\">\n                        <field_name>sum(ohrm_leave_adjustment.no_of_days)</field_name>\n                        <field_alias>adjustments</field_alias>\n                        <display_name>Leave Adjustments (Days)</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"false\">\n                        <field_name>count(ohrm_leave_adjustment.id)</field_name>\n                        <field_alias>adjustment_count</field_alias>\n                        <display_name>Adjustment Count</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"false\">\n                        <field_name>sum(IF(ohrm_leave_adjustment.adjustment_type = 3, -- LEAVE_ENTITLEMENT_TYPE_CARRIED_FORWARD\n                            ohrm_leave_adjustment.no_of_days, 0))</field_name>\n                        <field_alias>carried_forward</field_alias>\n                        <display_name>Carried Forward</display_name>\n                        <align>right</align>\n                        <width>1</width>\n                    </field>\n                </fields>\n            </display_group>\n        </display_groups>\n    </sub_report>\n\n    <sub_report type=\"sql\" name=\"timeline\">\n        <query>FROM ohrm_leave_type WHERE deleted = 0 ORDER BY ohrm_leave_type.id</query>\n        <id_field>leaveTypeId</id_field>\n        <display_groups>\n            <display_group name=\"rest\" type=\"one\" display=\"true\">\n                <group_header></group_header>\n                <fields>\n                    <field display=\"false\">\n                        <field_name>ohrm_leave_type.id</field_name>\n                        <field_alias>leaveTypeId</field_alias>\n                        <display_name>Leave Type ID</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"true\">\n                        <field_name>ohrm_leave_type.name</field_name>\n                        <field_alias>timeline</field_alias>\n                        <display_name> </display_name>\n                        <width>25</width>\n                        <align>right</align>\n                        <link><href>leave.balance_report.timeline({employeeId: $P{empNumber},leaveTypeId:$P{leaveTypeId}})</href></link>\n                    </field>\n                </fields>\n            </display_group>\n        </display_groups>\n    </sub_report>\n\n    <join>\n        <join_by sub_report=\"mainTable\" id=\"leaveTypeId\"></join_by>\n        <join_by sub_report=\"current_period\" id=\"leaveTypeId\"></join_by>\n        <join_by sub_report=\"entitlementsTotal\" id=\"leaveTypeId\"></join_by>\n        <join_by sub_report=\"pendingQuery\" id=\"leaveTypeId\"></join_by>\n        <join_by sub_report=\"scheduledQuery\" id=\"leaveTypeId\"></join_by>\n        <join_by sub_report=\"takenQuery\" id=\"leaveTypeId\"></join_by>\n        <join_by sub_report=\"unused\" id=\"leaveTypeId\"></join_by>\n        <join_by sub_report=\"overdrawn\" id=\"leaveTypeId\"></join_by>\n        <join_by sub_report=\"adjustments\" id=\"leaveTypeId\"></join_by>\n        <join_by sub_report=\"timeline\" id=\"leaveTypeId\"></join_by>\n    </join>\n    <page_limit>100</page_limit>\n</report>', 1);
INSERT INTO `ohrm_advanced_report` (`id`, `name`, `unique_name`, `definition`, `enabled`) VALUES
(17, 'Leave Entitlements and Usage Report', 'Leave_Entitlements_and_Usage_Report_By_LeaveType', '<report>\n    <settings>\n        <csv>\n            <include_group_header>1</include_group_header>\n            <include_header>1</include_header>\n        </csv>\n    </settings>\n    <filter_fields>\n        <input_field type=\"text\" name=\"leaveType\" label=\"Leave Type\"></input_field>\n        <input_field type=\"text\" name=\"fromDate\" label=\"From\"></input_field>\n        <input_field type=\"text\" name=\"toDate\" label=\"To\"></input_field>\n        <input_field type=\"text\" name=\"asOfDate\" label=\"AsOf\"></input_field>\n        <input_field type=\"text\" name=\"emp_numbers\" label=\"employees\"></input_field>\n        <input_field type=\"text\" name=\"job_title\" label=\"Job Title\"></input_field>\n        <input_field type=\"text\" name=\"location\" label=\"Location\"></input_field>\n        <input_field type=\"text\" name=\"sub_unit\" label=\"Sub Unit\"></input_field>\n        <input_field type=\"text\" name=\"terminated\" label=\"Terminated\"></input_field>\n        <input_field type=\"text\" name=\"country_code\" label=\"Country Code\"></input_field>\n        <input_field type=\"text\" name=\"period_offset\" label=\"Leave Period Offset\"></input_field>\n    </filter_fields>\n\n    <sub_report type=\"sql\" name=\"mainTable\">\n        <query>FROM hs_hr_employee\n            LEFT JOIN hs_hr_emp_locations ON hs_hr_employee.emp_number = hs_hr_emp_locations.emp_number\n            LEFT JOIN ohrm_location ON ohrm_location.id = hs_hr_emp_locations.location_id\n            LEFT JOIN ohrm_subunit ON ohrm_subunit.id = hs_hr_employee.work_station\n            LEFT JOIN ohrm_job_title ON ohrm_job_title.id = hs_hr_employee.job_title_code\n            LEFT JOIN ohrm_leave_type ON $X{=,ohrm_leave_type.id,leaveType}\n            WHERE $X{IN,hs_hr_employee.emp_number,emp_numbers}\n            AND $X{=,hs_hr_employee.job_title_code,job_title}\n            AND $X{IN,hs_hr_employee.work_station,sub_unit}\n            AND $X{IN,hs_hr_emp_locations.location_id,location}\n            AND $X{=,ohrm_location.country_code,country_code}\n            AND $X{IS NULL,hs_hr_employee.termination_id,terminated}\n            ORDER BY hs_hr_employee.emp_lastname</query>\n        <id_field>empNumber</id_field>\n        <display_groups>\n            <display_group name=\"left_group\" type=\"one\" display=\"true\">\n                <group_header></group_header>\n                <fields>\n                    <field display=\"false\">\n                        <field_name>hs_hr_employee.emp_number</field_name>\n                        <field_alias>empNumber</field_alias>\n                        <display_name>Employee Number</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"false\">\n                        <field_name>hs_hr_employee.termination_id</field_name>\n                        <field_alias>termination_id</field_alias>\n                        <display_name>Termination ID</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"false\">\n                        <field_name>ohrm_leave_type.name</field_name>\n                        <field_alias>leaveTypeName</field_alias>\n                        <display_name>Leave Type</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"false\">\n                        <field_name>hs_hr_employee.employee_id</field_name>\n                        <field_alias>employeeId</field_alias>\n                        <display_name>ID</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"false\">\n                        <field_name>ohrm_job_title.job_title</field_name>\n                        <field_alias>jobTitle</field_alias>\n                        <display_name>Job Title</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"false\">\n                        <field_name>ohrm_subunit.name</field_name>\n                        <field_alias>subunit</field_alias>\n                        <display_name>Sub Unit</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"false\">\n                        <field_name>ohrm_location.name</field_name>\n                        <field_alias>location</field_alias>\n                        <display_name>Location</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"true\">\n                        <field_name>CONCAT(hs_hr_employee.emp_firstname, \' \', hs_hr_employee.emp_lastname)</field_name>\n                        <field_alias>employeeName</field_alias>\n                        <display_name>Employee</display_name>\n                        <width>150</width>\n                    </field>\n                </fields>\n            </display_group>\n        </display_groups>\n    </sub_report>\n    <sub_report type=\"sql\" name=\"current_period\">\n        <query>FROM hs_hr_employee ORDER BY hs_hr_employee.emp_number</query>\n        <id_field>empNumber</id_field>\n        <display_groups>\n            <display_group name=\"left_group\" type=\"one\" display=\"true\">\n                <group_header></group_header>\n                <fields>\n                    <field display=\"false\">\n                        <field_name>hs_hr_employee.emp_number</field_name>\n                        <field_alias>empNumber</field_alias>\n                        <display_name>Employee Number</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"false\">\n                        <field_name>hs_hr_employee.emp_number</field_name>\n                        <field_alias>fromDate</field_alias>\n                        <display_name>From Date</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"false\">\n                        <field_name>hs_hr_employee.emp_number</field_name>\n                        <field_alias>toDate</field_alias>\n                        <display_name>To Date</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"true\">\n                        <field_name>hs_hr_employee.emp_firstname</field_name>\n                        <field_alias>current</field_alias>\n                        <display_name>Leave Period</display_name>\n                        <width>85</width>\n                    </field>\n\n                </fields>\n            </display_group>\n        </display_groups>\n    </sub_report>\n    <sub_report type=\"sql\" name=\"entitlementsTotal\">\n        <query>\n\n            FROM (\n            SELECT ohrm_leave_entitlement.id as id,\n            ohrm_leave_entitlement.emp_number as emp_number,\n            sum(IF(validOnAsOfDateForLeavePeriod(ohrm_leave_entitlement.from_date, ohrm_leave_entitlement.to_date, ohrm_leave_entitlement.emp_number, $X{VAR,leaveType}, $X{VAR,period_offset}), ohrm_leave_entitlement.no_of_days, ohrm_leave_entitlement.days_used)) AS available\n            FROM ohrm_leave_entitlement\n            WHERE ohrm_leave_entitlement.deleted=0 AND $X{=,ohrm_leave_entitlement.leave_type_id,leaveType}\n            AND $X{IN,ohrm_leave_entitlement.emp_number,empNumber} AND\n            overlapsWithLeavePeriod(ohrm_leave_entitlement.from_date, ohrm_leave_entitlement.to_date, ohrm_leave_entitlement.emp_number, $X{VAR,leaveType}, $X{VAR,period_offset})\n\n            GROUP BY ohrm_leave_entitlement.id\n            ) AS A\n\n            GROUP BY A.emp_number\n            ORDER BY A.emp_number\n\n        </query>\n        <id_field>empNumber</id_field>\n        <display_groups>\n            <display_group name=\"left_group\" type=\"one\" display=\"true\">\n                <group_header></group_header>\n                <fields>\n                    <field display=\"false\">\n                        <field_name>A.emp_number</field_name>\n                        <field_alias>empNumber</field_alias>\n                        <display_name>Emp Number</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"false\">\n                        <field_name>count(id)</field_name>\n                        <field_alias>entitlement_count</field_alias>\n                        <display_name>Entitlement Count</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"true\">\n                        <field_name>sum(A.available)</field_name>\n                        <field_alias>entitlement_total</field_alias>\n                        <display_name>Entitlements Days</display_name>\n                        <width>90</width>\n                        <align>right</align>\n                        <link><href>leave.balance_report.view_leave_entitlement({employeeId:$P{empNumber},leaveTypeId:$P{leaveTypeId},from:\'$P{fromDate}\',to:\'$P{toDate}\',id:0})</href></link>\n                    </field>\n                </fields>\n            </display_group>\n        </display_groups>\n    </sub_report>\n\n    <sub_report type=\"sql\" name=\"pendingQuery\">\n        <query>\n            FROM ohrm_leave\n            LEFT JOIN ohrm_leave_leave_entitlement ON ohrm_leave_leave_entitlement.leave_id = ohrm_leave.id\n            WHERE $X{=,ohrm_leave.leave_type_id,leaveType} AND\n            status IN (SELECT s.status FROM ohrm_leave_status s WHERE s.name LIKE \'%PENDING APPROVAL%\') AND\n            $X{IN,ohrm_leave.emp_number,empNumber} AND\n            withinLeavePeriod(ohrm_leave.date, ohrm_leave.emp_number, $X{VAR,leaveType}, $X{VAR,period_offset})\n\n            GROUP BY emp_number\n            ORDER BY ohrm_leave.emp_number\n        </query>\n        <id_field>empNumber</id_field>\n        <display_groups>\n            <display_group name=\"entitlement_usage\" type=\"one\" display=\"true\" show_header=\"true\">\n                <group_header>Usage of Entitlements</group_header>\n                <fields>\n                    <field display=\"false\">\n                        <field_name>ohrm_leave.emp_number</field_name>\n                        <field_alias>empNumber</field_alias>\n                        <display_name>Emp Number</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"false\">\n                        <field_name>GROUP_CONCAT(DISTINCT ohrm_leave.leave_request_id)</field_name>\n                        <field_alias>pendingLeaveRequests</field_alias>\n                        <display_name>Pending Leave Requests</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"true\">\n                        <field_name>sum(IFNULL(ohrm_leave_leave_entitlement.length_days,0))</field_name>\n                        <field_alias>pending</field_alias>\n                        <display_name>Pending Approval</display_name>\n                        <width>67</width>\n                        <align>right</align>\n                        <link><href>leave.balance_report.view_leave_list({filter: \'$P{pendingLeaveRequests}\',showOverdrawn: 1,hideEmployeeColumn: true})</href></link>\n                    </field>\n                </fields>\n            </display_group>\n        </display_groups>\n    </sub_report>\n\n\n    <sub_report type=\"sql\" name=\"scheduledQuery\">\n        <query>\n            FROM ohrm_leave\n            LEFT JOIN ohrm_leave_leave_entitlement ON ohrm_leave_leave_entitlement.leave_id = ohrm_leave.id\n            WHERE $X{=,ohrm_leave.leave_type_id,leaveType} AND\n            status = 2 AND\n            $X{IN,ohrm_leave.emp_number,empNumber} AND\n            withinLeavePeriod(ohrm_leave.date, ohrm_leave.emp_number, $X{VAR,leaveType}, $X{VAR,period_offset})\n\n            GROUP BY emp_number\n            ORDER BY ohrm_leave.emp_number\n        </query>\n        <id_field>empNumber</id_field>\n        <display_groups>\n            <display_group name=\"entitlement_usage\" type=\"one\" display=\"true\" show_header=\"true\">\n                <group_header>Usage of entitlements</group_header>\n                <fields>\n                    <field display=\"false\">\n                        <field_name>ohrm_leave.emp_number</field_name>\n                        <field_alias>empNumber</field_alias>\n                        <display_name>Emp Number</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"false\">\n                        <field_name>GROUP_CONCAT(DISTINCT ohrm_leave.leave_request_id)</field_name>\n                        <field_alias>scheduledLeaveRequests</field_alias>\n                        <display_name>Scheduled Leave Requests</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"true\">\n                        <field_name>sum(IFNULL(ohrm_leave_leave_entitlement.length_days,0))</field_name>\n                        <field_alias>scheduled</field_alias>\n                        <display_name>Scheduled</display_name>\n                        <width>80</width>\n                        <align>right</align>\n                        <link><href>leave.balance_report.view_leave_list({filter: \'$P{scheduledLeaveRequests}\',showOverdrawn: 1,hideEmployeeColumn: true})</href></link>\n                    </field>\n                </fields>\n            </display_group>\n        </display_groups>\n    </sub_report>\n\n    <sub_report type=\"sql\" name=\"takenQuery\">\n        <query>\n            FROM ohrm_leave\n            LEFT JOIN ohrm_leave_leave_entitlement ON ohrm_leave_leave_entitlement.leave_id = ohrm_leave.id\n            WHERE $X{=,ohrm_leave.leave_type_id,leaveType} AND\n            status = 3 AND\n            $X{IN,ohrm_leave.emp_number,empNumber} AND\n            withinLeavePeriod(ohrm_leave.date, ohrm_leave.emp_number, $X{VAR,leaveType}, $X{VAR,period_offset})\n\n            GROUP BY emp_number\n            ORDER BY ohrm_leave.emp_number\n        </query>\n        <id_field>empNumber</id_field>\n        <display_groups>\n            <display_group name=\"entitlement_usage\" type=\"one\" display=\"true\" show_header=\"true\">\n                <group_header>Usage of entitlements</group_header>\n                <fields>\n                    <field display=\"false\">\n                        <field_name>ohrm_leave.emp_number</field_name>\n                        <field_alias>empNumber</field_alias>\n                        <display_name>Emp Number</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"false\">\n                        <field_name>GROUP_CONCAT(DISTINCT ohrm_leave.leave_request_id)</field_name>\n                        <field_alias>takenLeaveRequests</field_alias>\n                        <display_name>Taken Leave Requests</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"true\">\n                        <field_name>sum(IFNULL(ohrm_leave_leave_entitlement.length_days,0))</field_name>\n                        <field_alias>taken</field_alias>\n                        <display_name>Taken</display_name>\n                        <width>60</width>\n                        <align>right</align>\n                        <link><href>leave.balance_report.view_leave_list({filter: \'$P{takenLeaveRequests}\',showOverdrawn: 1,hideEmployeeColumn: true})</href></link>\n                    </field>\n                </fields>\n            </display_group>\n        </display_groups>\n    </sub_report>\n    <sub_report type=\"sql\" name=\"unused\">\n        <query>FROM hs_hr_employee WHERE $X{IN,hs_hr_employee.emp_number,empNumber} ORDER BY hs_hr_employee.emp_number</query>\n        <id_field>empNumber</id_field>\n        <display_groups>\n            <display_group name=\"rest\" type=\"one\" display=\"true\">\n                <group_header></group_header>\n                <fields>\n                    <field display=\"false\">\n                        <field_name>hs_hr_employee.emp_number</field_name>\n                        <field_alias>empNumber</field_alias>\n                        <display_name>Employee Number</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"true\">\n                        <field_name>hs_hr_employee.emp_firstname</field_name>\n                        <field_alias>unused</field_alias>\n                        <display_name>Available Balance</display_name>\n                        <width>70</width>\n                        <align>right</align>\n                    </field>\n\n                </fields>\n            </display_group>\n        </display_groups>\n    </sub_report>\n    <sub_report type=\"sql\" name=\"overdrawn\">\n        <query>\n            FROM (\n            SELECT\n\n            ohrm_leave.emp_number as emp_number,\n            ohrm_leave.id as leave_id,\n            ohrm_leave.leave_request_id as leave_request_id,\n            ohrm_leave.leave_type_id as leave_type_id,\n            ohrm_leave.length_days as leave_length_days,\n            sum(IFNULL(ohrm_leave_leave_entitlement.length_days,0)) AS entitlement_length_days\n\n            FROM ohrm_leave\n            LEFT JOIN ohrm_leave_leave_entitlement ON ohrm_leave_leave_entitlement.leave_id = ohrm_leave.id\n            WHERE $X{=,ohrm_leave.leave_type_id,leaveType} AND\n            $X{IN,ohrm_leave.emp_number,empNumber} AND\n            status NOT IN(-1, 0, 4, 5) AND\n            withinLeavePeriod(ohrm_leave.date, ohrm_leave.emp_number, $X{VAR,leaveType}, $X{VAR,period_offset})\n\n            GROUP BY ohrm_leave.id\n\n            ) AS OD\n\n            GROUP BY emp_number\n            ORDER BY emp_number\n        </query>\n        <id_field>empNumber</id_field>\n        <display_groups>\n            <display_group name=\"rest\" type=\"one\" display=\"true\">\n                <group_header></group_header>\n                <fields>\n                    <field display=\"false\">\n                        <field_name>emp_number</field_name>\n                        <field_alias>empNumber</field_alias>\n                        <display_name>Emp Number</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"false\">\n                        <field_name>GROUP_CONCAT(DISTINCT IF(leave_length_days > IFNULL(entitlement_length_days,0),leave_request_id, NULL))</field_name>\n                        <field_alias>overdrawn_leave_requests</field_alias>\n                        <display_name>Overdrawn Leave Requests</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"true\">\n                        <field_name>sum(leave_length_days) - sum(IFNULL(entitlement_length_days,0))</field_name>\n                        <field_alias>overdrawn</field_alias>\n                        <display_name>Overdrawn Days</display_name>\n                        <description>Leave without entitlements</description>\n                        <width>80</width>\n                        <align>right</align>\n                        <link><href>leave.balance_report.view_leave_list({filter: \'$P{overdrawn_leave_requests}\',showOverdrawn: 1,skipFullyOverdrawn: false,hideEmployeeColumn: true})</href></link>\n                    </field>\n                </fields>\n            </display_group>\n        </display_groups>\n    </sub_report>\n\n    <sub_report type=\"sql\" name=\"adjustments\">\n        <query>\n            FROM ohrm_leave_adjustment\n\n            WHERE ohrm_leave_adjustment.deleted=0 AND $X{=,ohrm_leave_adjustment.leave_type_id,leaveType}\n            AND $X{IN,ohrm_leave_adjustment.emp_number,empNumber} AND\n            overlapsWithLeavePeriod(ohrm_leave_adjustment.from_date, ohrm_leave_adjustment.to_date, ohrm_leave_adjustment.emp_number, $X{VAR,leaveType}, $X{VAR,period_offset})\n\n            GROUP BY emp_number\n            ORDER BY ohrm_leave_adjustment.emp_number\n\n        </query>\n        <id_field>empNumber</id_field>\n        <display_groups>\n            <display_group name=\"rest\" type=\"one\" display=\"true\">\n                <group_header></group_header>\n                <fields>\n                    <field display=\"false\">\n                        <field_name>ohrm_leave_adjustment.emp_number</field_name>\n                        <field_alias>empNumber</field_alias>\n                        <display_name>Emp Number</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"false\">\n                        <field_name>sum(ohrm_leave_adjustment.no_of_days)</field_name>\n                        <field_alias>adjustments</field_alias>\n                        <display_name>Leave Adjustments (Days)</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"false\">\n                        <field_name>count(ohrm_leave_adjustment.id)</field_name>\n                        <field_alias>adjustment_count</field_alias>\n                        <display_name>Adjustment Count</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"false\">\n                        <field_name>sum(IF(ohrm_leave_adjustment.adjustment_type = 3, -- LEAVE_ENTITLEMENT_TYPE_CARRIED_FORWARD\n                            ohrm_leave_adjustment.no_of_days, 0))</field_name>\n                        <field_alias>carried_forward</field_alias>\n                        <display_name>Carried Forward Days</display_name>\n                        <align>right</align>\n                        <width>100</width>\n                    </field>\n                </fields>\n            </display_group>\n        </display_groups>\n    </sub_report>\n\n    <sub_report type=\"sql\" name=\"timeline\">\n        <query>FROM hs_hr_employee ORDER BY hs_hr_employee.emp_number</query>\n        <id_field>empNumber</id_field>\n        <display_groups>\n            <display_group name=\"rest\" type=\"one\" display=\"true\">\n                <group_header></group_header>\n                <fields>\n                    <field display=\"false\">\n                        <field_name>hs_hr_employee.emp_number</field_name>\n                        <field_alias>empNumber</field_alias>\n                        <display_name>Employee Number</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"true\">\n                        <field_name>hs_hr_employee.emp_firstname</field_name>\n                        <field_alias>timeline</field_alias>\n                        <display_name> </display_name>\n                        <width>25</width>\n                        <link><href>leave.balance_report.timeline({employeeId: $P{empNumber},leaveTypeId:$P{leaveTypeId}})</href></link>\n                    </field>\n\n                </fields>\n            </display_group>\n        </display_groups>\n    </sub_report>\n    <join>\n        <join_by sub_report=\"mainTable\" id=\"empNumber\"></join_by>\n        <join_by sub_report=\"current_period\" id=\"empNumber\"></join_by>\n        <join_by sub_report=\"entitlementsTotal\" id=\"empNumber\"></join_by>\n        <join_by sub_report=\"pendingQuery\" id=\"empNumber\"></join_by>\n        <join_by sub_report=\"scheduledQuery\" id=\"empNumber\"></join_by>\n        <join_by sub_report=\"takenQuery\" id=\"empNumber\"></join_by>\n        <join_by sub_report=\"unused\" id=\"empNumber\"></join_by>\n        <join_by sub_report=\"overdrawn\" id=\"empNumber\"></join_by>\n        <join_by sub_report=\"adjustments\" id=\"empNumber\"></join_by>\n        <join_by sub_report=\"timeline\" id=\"empNumber\"></join_by>\n    </join>\n    <page_limit>20</page_limit>\n</report>', 1),
(18, 'Leave Percentage Report', 'Leave_Percentage_Report', '<report>\n    <settings>\n        <csv>\n            <include_group_header>1</include_group_header>\n            <include_header>1</include_header>\n        </csv>\n    </settings>\n    <filter_fields>\n        <input_field type=\"leave_type\" name=\"leave_types\" label=\"Leave Type\" required=\"false\" allow-multiple=\"true\">\n            <options>\n                <choices type=\"sql\">\n                    <query>SELECT lt.id, lt.name AS name, c.cou_name as country FROM ohrm_leave_type lt LEFT JOIN ohrm_operational_country oc ON oc.id = lt.operational_country_id LEFT JOIN hs_hr_country c ON c.cou_code = oc.country_code WHERE lt.deleted =0 ORDER BY lt.name</query>\n                    <value>id</value>\n                    <label>name</label>\n                </choices>\n            </options>\n        </input_field>\n        <input_field type=\"date_range\" name=\"leave_period\" label=\"Leave Date\" required=\"false\" allow-multiple=\"false\">        \n        </input_field>\n    </filter_fields> \n\n    <sub_report type=\"sql\" name=\"mainTable\">\n        <query>\n            From (SELECT l.leave_type_id,  count(DISTINCT l.emp_number) as leavecount, ec.empcount\n            FROM ohrm_leave l, ( \n            SELECT count( * ) AS empcount\n            FROM hs_hr_employee where termination_id is null ) ec \n            where  \n           $X{IN,l.leave_type_id,leave_types} AND\n            l.status IN (2, 3) AND \n            $X{BETWEEN,l.date,leave_period} AND\n            l.emp_number in (select emp_number from hs_hr_employee where termination_id is null) \n            GROUP BY l.leave_type_id) as pe\n            LEFT JOIN ohrm_leave_type lt on lt.id = pe.leave_type_id\n            LEFT JOIN ohrm_operational_country oc on oc.id = lt.operational_country_id\n            LEFT JOIN hs_hr_country c on c.cou_code = oc.country_code\n            LEFT JOIN (\n                     SELECT count( distinct(e.`emp_number`) ) AS cempcount , oc.country_code AS cid FROM `hs_hr_employee` e  LEFT JOIN  hs_hr_emp_locations el on  e.`emp_number` = el.`emp_number`   LEFT JOIN  `ohrm_location`  ol on el.`location_id`= ol.`id`  LEFT JOIN ohrm_operational_country oc on ol.country_code = oc.country_code  where e.termination_id is null  group by oc.country_code\n     ) cec on cec.cid = c.cou_code\n\n        </query>\n        <display_groups>\n            <display_group name=\"leavePercentageInformation\" type=\"one\" display=\"true\">\n                <group_header></group_header>\n                <fields>\n                    <field display=\"true\">\n                        <field_name>if((SELECT value FROM ohrm_config WHERE property = \"leave.country_based\") = \"on\", concat(c.cou_name, \" - \", lt.name), lt.name)</field_name>\n                        <field_alias>leaveType</field_alias>\n                        <display_name>Leave Type</display_name>\n                        <width>200</width>\n                    </field> \n                    <field display=\"true\">\n                        <field_name>if((SELECT value FROM ohrm_config WHERE property = \"leave.country_based\") = \"on\", format(100 * (pe.leavecount / cec.cempcount), 2) , format(100 * (pe.leavecount / pe.empcount), 2))</field_name>\n                        <field_alias>percentage</field_alias>\n                        <display_name>Percentage (%)</display_name>\n                        <width>150</width>\n                        <align>right</align>\n                    </field> \n                </fields>\n            </display_group>\n        </display_groups>\n    </sub_report>\n    <page_limit>20</page_limit>       \n</report>', 1),
(19, 'Leave Bradford Factor Report', 'Bradford_Factor_Report', '<report>\n    <settings>\n        <csv>\n            <include_group_header>1</include_group_header>\n            <include_header>1</include_header>\n        </csv>\n        <data_group>leave_bradford_factor_report</data_group>\n        <sessionvariables>\n            <sessionvariable>i = 0</sessionvariable>\n            <sessionvariable>last_dt = NULL</sessionvariable>\n            <sessionvariable>last_user = NULL</sessionvariable>\n        </sessionvariables>\n        <reportcountstyle>php</reportcountstyle>\n    </settings>\n    <filter_fields>\n        <input_field type=\"employee\" name=\"employee_name\" label=\"Employee Name\" allow-multiple=\"true\">\n            <options>\n                <search_mode>id</search_mode>\n            </options>\n        </input_field>\n        <input_field type=\"select\" name=\"location\" label=\"Location\" allow-multiple=\"true\">\n            <options>\n                <excludeAllOption>true</excludeAllOption>\n                <choices type=\"sql\">\n                    <query>SELECT id, name FROM ohrm_location ORDER BY name</query>\n                    <value>id</value>\n                    <label>name</label>\n                </choices>\n            </options>\n        </input_field>\n\n        <input_field type=\"select\" name=\"job_category\" label=\"Job Category\" allow-multiple=\"true\">\n            <options>\n                <excludeAllOption>true</excludeAllOption>\n                <choices type=\"sql\">\n                    <query>SELECT id, name FROM ohrm_job_category ORDER BY id</query>\n                    <value>id</value>\n                    <label>name</label>\n                </choices>\n            </options>\n        </input_field>\n        <input_field type=\"sub_unit\" name=\"sub_unit\" label=\"Sub Unit\" required=\"false\" allow-multiple=\"true\">\n            <options>\n                <excludeAllOption>true</excludeAllOption>\n                <choices type=\"sql\">\n                    <query>SELECT id, name, level FROM ohrm_subunit where id != 1 ORDER BY lft</query>\n                    <value>id</value>\n                    <label>name</label>\n                </choices>\n            </options>\n        </input_field>\n        <input_field type=\"select\" name=\"job_title\" label=\"Job Title\" allow-multiple=\"true\">\n            <options>\n                <excludeAllOption>true</excludeAllOption>\n                <choices type=\"sql\">\n                    <query>SELECT id, job_title FROM ohrm_job_title ORDER BY id</query>\n                    <value>id</value>\n                    <label>job_title</label>\n                </choices>\n            </options>\n\n        </input_field>\n        <input_field type=\"supervisor\" name=\"supervisors\" label=\"Supervisors\" allow-multiple=\"true\">\n            <options>\n                <search_mode>id</search_mode>\n            </options>\n        </input_field>\n\n        <input_field type=\"leave_period\" name=\"leave_period\" label=\"Leave Period\" allow-multiple=\"false\"/>\n        <input_field type=\"select\" name=\"include\" label=\"Include\" required=\"true\" allow-multiple=\"false\">\n            <options>\n                <excludeAllOption>true</excludeAllOption>\n                <choices type=\"list\">\n                    <option value=\"1\">Current Employees Only</option>\n                    <option value=\"2\">Current and Past Employees</option>\n                    <option value=\"3\">Past Employees Only</option>\n                </choices>\n            </options>\n            <default_value>\"1\"</default_value>\n        </input_field>\n    </filter_fields>\n\n    <sub_report type=\"sql\" name=\"mainTable\">\n        <query>FROM\n            (SELECT ohrm_leave.`emp_number`,\n            CONCAT(hs_hr_employee.emp_firstname, \' \', hs_hr_employee.emp_lastname) as emp_name ,\n            ohrm_job_title.job_title as job_title,\n            ohrm_subunit.name as subunit_name,\n            ohrm_location.name  as location_name,\n            ohrm_location.id,\n            hs_hr_employee.work_station,\n            hs_hr_employee.job_title_code,\n            hs_hr_employee.termination_id,\n            hs_hr_employee.eeo_cat_code,\n            hs_hr_emp_reportto.erep_sup_emp_number,\n            ohrm_leave.`date`,\n            @i := IF(@last_user IS NULL OR @last_user &lt;&gt; ohrm_leave.`emp_number`, 1, IF(@last_dt IS NULL OR (DATE(ohrm_leave.`date`) - INTERVAL 1 DAY) &gt;  DATE(@last_dt), @i + 1, @i)) AS days,\n            @last_dt := DATE(ohrm_leave.`date`),\n            @last_user := ohrm_leave.emp_number\n            FROM\n            ohrm_leave\n            LEFT JOIN hs_hr_employee ON ohrm_leave.emp_number = hs_hr_employee.emp_number\n            LEFT JOIN hs_hr_emp_locations ON hs_hr_employee.emp_number = hs_hr_emp_locations.emp_number\n            LEFT JOIN ohrm_location ON ohrm_location.id = hs_hr_emp_locations.location_id\n            LEFT JOIN ohrm_subunit ON ohrm_subunit.id = hs_hr_employee.work_station\n            LEFT JOIN ohrm_job_title ON ohrm_job_title.id = hs_hr_employee.job_title_code\n            LEFT JOIN ohrm_job_category ON (hs_hr_employee.eeo_cat_code = ohrm_job_category.id)\n            LEFT JOIN hs_hr_emp_reportto ON (hs_hr_employee.emp_number = hs_hr_emp_reportto.erep_sub_emp_number)\n            WHERE\n\n\n             ohrm_leave.`status` in (1,2,3)\n            AND ohrm_leave.leave_type_id in (SELECT id  FROM ohrm_leave_type WHERE include_in_bradford_factor_report = 1)\n            AND $X{BETWEEN,ohrm_leave.`date`,leave_period}\n            ORDER BY\n            ohrm_leave.`emp_number`,ohrm_leave.`date`\n            ) v\n            WHERE $X{IN,v.emp_number,employee_name}\n            AND $X{IN,v.work_station,sub_unit}\n            AND $X{IN,v.id,location}\n            AND $X{IN,v.job_title_code,job_title}\n            AND $X{IN,v.eeo_cat_code,job_category}\n            AND $X{IN,v.erep_sup_emp_number,supervisors}\n            AND IF($X{VAR,include} IS NULL, true, CASE $X{VAR,include}\n            WHEN 1 then termination_id IS NULL -- current employees only\n            WHEN 2 then TRUE -- current and past employees\n            ELSE termination_id IS NOT NULL -- past employees only\n            END)\n            AND $X{ACCESSIBLE_ENTITY_ID,v.emp_number,Employee}\n            GROUP BY\n            v.`emp_number`\n            ORDER BY\n            (MAX(days) * MAX(days) * COUNT(DISTINCT(DATE(`date`)))) DESC\n             </query>\n        <id_field>empNumber</id_field>\n        <display_groups>\n            <display_group name=\"left_group\" type=\"one\" display=\"true\">\n                <group_header></group_header>\n                <fields>\n                    <field display=\"false\">\n                        <field_name>v.emp_number</field_name>\n                        <field_alias>empNumber</field_alias>\n                        <display_name>Employee Number</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"true\">\n                        <field_name>v.emp_name</field_name>\n                        <field_alias>employeeName</field_alias>\n                        <display_name>Employee</display_name>\n                        <width>150</width>\n                    </field>\n                    <field display=\"true\">\n                        <field_name>v.job_title</field_name>\n                        <field_alias>jobTitle</field_alias>\n                        <display_name>Job Title</display_name>\n                        <width>150</width>\n                    </field>\n                    <field display=\"true\">\n                        <field_name>v.subunit_name</field_name>\n                        <field_alias>subunit</field_alias>\n                        <display_name>Sub Unit</display_name>\n                        <width>100</width>\n                    </field>\n                    <field display=\"true\">\n                        <field_name>v.location_name</field_name>\n                        <field_alias>location</field_alias>\n                        <display_name>Location</display_name>\n                        <width>100</width>\n                    </field>\n                    <field display=\"true\">\n                        <field_name>COUNT(DISTINCT(DATE(`date`)))</field_name>\n                        <field_alias>number_of_days</field_alias>\n                        <display_name>Number of Days</display_name>\n                        <width>70</width>\n                        <align>right</align>\n                    </field>\n                    <field display=\"true\">\n                        <field_name>MAX(days)</field_name>\n                        <field_alias>number_of_spels</field_alias>\n                        <display_name>Number of Spells</display_name>\n                        <format>link</format>\n                        <link><href>leave.balance_report.view_leave_list({filter: \'$P{leave_request_ids}\',showOverdrawn: 1,skipFullyOverdrawn: false,hideEmployeeColumn: true,no_ext_link:true})</href></link>\n                        <width>70</width>\n                        <align>right</align>\n                    </field>\n                    <field display=\"true\">\n                        <field_name>(MAX(days) * MAX(days) * COUNT(DISTINCT(DATE(`date`))))</field_name>\n                        <field_alias>bradford_factor</field_alias>\n                        <display_name>Bradford Factor</display_name>\n                        <width>70</width>\n                        <align>right</align>\n                    </field>\n\n                </fields>\n            </display_group>\n        </display_groups>\n    </sub_report>\n    <sub_report type=\"sql\" name=\"current_period\">\n        <query>\n            FROM ohrm_leave\n            WHERE\n            ohrm_leave.`status` in (1,2,3)\n            AND ohrm_leave.leave_type_id in (SELECT id  FROM ohrm_leave_type WHERE include_in_bradford_factor_report = 1)\n            AND $X{BETWEEN,ohrm_leave.`date`,leave_period}\n            GROUP BY ohrm_leave.emp_number</query>\n        <id_field>empNumber</id_field>\n        <display_groups>\n            <display_group name=\"rest\" type=\"one\" display=\"true\">\n                <group_header></group_header>\n                <fields>\n                    <field display=\"false\">\n                        <field_name>ohrm_leave.emp_number</field_name>\n                        <field_alias>empNumber</field_alias>\n                        <display_name>Employee Number</display_name>\n                        <width>1</width>\n                    </field>\n                    <field display=\"false\">\n                        <field_name>GROUP_CONCAT(DISTINCT (ohrm_leave.leave_request_id) )</field_name>\n                        <field_alias>leave_request_ids</field_alias>\n                        <display_name>leave_request_ids</display_name>\n                        <width>1</width>\n                     </field>\n\n                </fields>\n            </display_group>\n        </display_groups>\n    </sub_report>\n    <join>\n    <join_by sub_report=\"mainTable\" id=\"empNumber\"></join_by>\n    <join_by sub_report=\"current_period\" id=\"empNumber\"></join_by>\n    </join>\n    <page_limit>20</page_limit>\n</report>\n', 1),
(20, 'Timesheet Report', 'Employee_Timesheet_Report', '<?xml version=\"1.0\"?>\n<report>\n    <settings>\n        <exportable>0</exportable>\n    </settings>\n    <caption>Timesheets Pending Action</caption>\n    <preload_results>1</preload_results>\n    <filter_fields>\n        <input_field type=\"employee\" name=\"employeeId\" label=\"Employee Name\" required=\"true\"> \n            <options> \n                <search_mode>id</search_mode>\n                <required_permissions>{\"data_group\":{\"time_employee_timesheets\":{\"canRead\":1,\"canCreate\":0,\"canUpdate\":0,\"canDelete\":0}}}</required_permissions>\n            </options> \n        </input_field>\n    </filter_fields>\n    <sub_report type=\"sql\" name=\"mainTable\">\n        <query>FROM ohrm_timesheet o\nLEFT JOIN  hs_hr_employee e ON o.employee_id = e.emp_number\n            WHERE $X{ACCESSIBLE_ENTITY_ID,e.emp_number,Employee,false}\nAND $X{=,e.emp_number,employeeId}\n            AND o.state = \"SUBMITTED\"\nORDER BY $Y{o.start_date DESC}</query>\n        <id_field>timesheetId</id_field>\n        <display_groups>\n            <display_group name=\"personalDetails\" type=\"one\" display=\"true\">\n                <fields>\n                    <field display=\"false\">\n                        <field_name>o.timesheet_id</field_name>\n                        <field_alias>timesheetId</field_alias>\n                        <display_name>Employee Number</display_name>\n                        <width>2</width>\n                    </field>\n                    <field display=\"false\">\n                        <field_name>o.start_date</field_name>\n                        <field_alias>timesheetStartday</field_alias>\n                        <display_name>Employee Number</display_name>\n                        <width>2</width>\n                    </field>\n                    <field display=\"false\">\n                        <field_name>o.employee_id</field_name>\n                        <field_alias>employeeId</field_alias>\n                        <display_name>Employee Number</display_name>\n                        <width>2</width>\n                    </field>\n                    <field display=\"true\">\n                        <field_name>concat_ws(\" \", e.emp_firstname, e.emp_lastname, IF(e.termination_id IS NULL, \'\', \' (Past Employee)\'))</field_name>\n                        <field_alias>employeeName</field_alias>\n                        <display_name>Employee Name</display_name>\n                        <sortField>employeeName</sortField>\n                        <width>40%</width>\n                    </field>\n                    <field display=\"true\">\n                        <field_name>concat_ws(\" to \", o.start_date, o.end_date)</field_name>\n                        <field_alias>timesheetPeriod</field_alias>\n                        <display_name>Timesheet Period</display_name>\n                        <sortField>timesheetPeriod</sortField>\n                        <width>54%</width>\n                        <format>date_range_with_to</format>\n                    </field>\n                    <field display=\"true\">\n                        <field_name>\"View\"</field_name>\n                        <field_alias>link</field_alias>\n                        <display_name> </display_name>\n                        <format>localize, link</format>\n                        <link>\n                            <href>time/viewPendingApprovelTimesheet?timesheetId=$P{timesheetId}&amp;employeeId=$P{employeeId}&amp;timesheetStartday=$P{timesheetStartday}</href>\n                        </link>\n                        <width>6%</width>\n                    </field>\n                </fields>\n            </display_group>\n        </display_groups>\n    </sub_report>\n    <page_limit>50</page_limit>\n</report>\n', 1);
INSERT INTO `ohrm_advanced_report` (`id`, `name`, `unique_name`, `definition`, `enabled`) VALUES
(21, 'Review and Appraisal Progress', 'Reviews and Appraisals Progress', '<?xml version=\"1.0\"?>\n<report>\n    <settings>\n        <csv>\n            <include_group_header>1</include_group_header>\n            <include_header>1</include_header>\n        </csv>\n    </settings>\n    <filter_fields>\n        <input_field type=\"employee\" name=\"employee_name\" label=\"Employee Name\" allow-multiple=\"true\"> \n            <options> \n                <search_mode>id</search_mode>\n            </options>\n        </input_field>\n        <input_field type=\"employee\" name=\"evaluators\" label=\"Evaluators\" allow-multiple=\"true\"> \n            <options> \n                <search_mode>id</search_mode>\n            </options> \n        </input_field>\n        <input_field type=\"select\" name=\"appraisal_status\" label=\"Appraisal Status\" allow-multiple=\"false\"> \n            <options> \n                <choices type=\"sql\"> \n                    <query>SELECT state_id, CONCAT(UCASE(LEFT(name, 1)),LCASE(SUBSTRING(name, 2))) AS name FROM ohrm_performance_appraisal_status ORDER BY state_id</query> \n                    <value>state_id</value> \n                    <label>name</label> \n                </choices>\n            </options> \n        </input_field> \n        <input_field type=\"sub_unit\" name=\"sub_unit\" label=\"Sub Unit\" required=\"false\" allow-multiple=\"true\"> \n            <options> \n                <choices type=\"sql\"> \n                    <query>SELECT id, name, level FROM ohrm_subunit where id != 1 ORDER BY lft</query> \n                    <value>id</value> \n                    <label>name</label> \n                </choices> \n            </options> \n        </input_field>\n        <input_field type=\"select\" name=\"location\" label=\"Location\" allow-multiple=\"true\"> \n            <options> \n                <choices type=\"sql\"> \n                    <query>SELECT id, name FROM ohrm_location ORDER BY name</query> \n                    <value>id</value> \n                    <label>name</label> \n                </choices>\n            </options> \n        </input_field>\n        <input_field type=\"supervisor\" name=\"supervisors\" label=\"Supervisors\" allow-multiple=\"true\">\n            <options> \n                <search_mode>id</search_mode>\n            </options> \n        </input_field>\n        <input_field type=\"date_range\" name=\"appraisal_period\" label=\"Appraisal Period\" allow-multiple=\"false\"/>\n        <input_field type=\"date_range\" name=\"appraisal_due_period\" label=\"Appraisal Due Date\" allow-multiple=\"false\"/>        \n        <input_field type=\"select\" name=\"review_status_input\" label=\"Review Status\" allow-multiple=\"false\"> \n            <options> \n                <choices type=\"sql\"> \n                    <query>SELECT state_id, CONCAT(UCASE(LEFT(name, 1)),LCASE(SUBSTRING(name, 2))) AS name FROM ohrm_performance_review_status ORDER BY state_id</query> \n                    <value>state_id</value> \n                    <label>name</label> \n                </choices>\n            </options> \n        </input_field>\n    </filter_fields>\n\n    <sub_report type=\"sql\" name=\"mainTable\">\n        <id_field>empNumber</id_field>\n        <query>FROM hs_hr_employee\n            LEFT JOIN hs_hr_emp_locations AS emp_location ON(hs_hr_employee.emp_number = emp_location.emp_number)\n            LEFT JOIN ohrm_location ON(emp_location.location_id = ohrm_location.id)\n            LEFT JOIN ohrm_subunit ON ohrm_subunit.id = hs_hr_employee.work_station\n            WHERE\n            $X{IN,hs_hr_employee.emp_number,employee_name}\n            AND $X{IN,hs_hr_employee.work_station,sub_unit}\n            AND $X{IN,ohrm_location.id,location}\n            AND $X{ACCESSIBLE_ENTITY_ID,hs_hr_employee.emp_number,Employee}\n        </query>\n        <display_groups>\n            <display_group name=\"employeeInformation\" type=\"one\" display=\"true\">\n                <group_header></group_header>\n                <fields>\n                    <field display=\"false\">\n                        <field_name>hs_hr_employee.emp_number</field_name>\n                        <field_alias>empNumber</field_alias>\n                        <display_name>Emp Number</display_name>\n                        <width>100</width>\n                    </field>\n                    <field display=\"true\">\n                        <field_name>hs_hr_employee.employee_id</field_name>\n                        <field_alias>employeeId</field_alias>\n                        <display_name>Employee Id</display_name>\n                        <width>100</width>\n                        <format/>\n                        <align/>\n                    </field>\n                    <field display=\"true\">\n                        <field_name>CONCAT(hs_hr_employee.emp_firstname, \" \", hs_hr_employee.emp_lastname)</field_name>\n                        <field_alias>employeeName</field_alias>\n                        <display_name>Employee Name</display_name>\n                        <width>200</width>\n                        <format/>\n                        <align/>\n                    </field>\n                </fields>                    \n            </display_group>\n        </display_groups>\n    </sub_report>\n    <sub_report type=\"sql\" name=\"supervisor_list\">\n        <id_field>empNumber</id_field>\n        <query>FROM hs_hr_emp_reportto AS supervisor_list\n            LEFT JOIN hs_hr_employee AS supervisor ON(supervisor.emp_number = supervisor_list.erep_sup_emp_number)             \n            WHERE $X{IN,supervisor_list.erep_sup_emp_number,supervisors}\n            AND supervisor.deleted_at IS NULL\n            GROUP BY supervisor_list.erep_sub_emp_number\n        </query>\n        <display_groups>\n            <display_group name=\"Supervisors_one\" type=\"one\" display=\"false\">\n                <fields>\n                    <field display=\"false\">\n                        <field_name>supervisor_list.erep_sub_emp_number</field_name>\n                        <field_alias>empNumber</field_alias>\n                        <display_name>Emp Number</display_name>\n                        <width>100</width>\n                    </field>\n                </fields>\n            </display_group>\n            <display_group name=\"Supervisors\" type=\"many\" display=\"true\">\n                <fields>\n                    <field display=\"true\">\n                        <field_name>CONCAT(supervisor.emp_firstname, \" \", supervisor.emp_lastname)</field_name>\n                        <field_alias/>\n                        <display_name>Supervisor Name</display_name>\n                        <width>200</width>\n                        <format/>\n                        <align/>\n                    </field>\n                    <field display=\"false\">\n                        <field_name>supervisor.emp_number</field_name>\n                        <field_alias/>\n                        <display_name>supervisorId</display_name>\n                        <width>100</width>\n                        <format/>\n                        <align/>\n                    </field>\n                </fields>\n            </display_group>                \n        </display_groups>    \n    </sub_report>\n    <sub_report type=\"sql\" name=\"appraisalInformation\">\n        <id_field>appraisalId</id_field>\n        <query>FROM ohrm_performance_appraisal AS appraisal_list\n            LEFT JOIN hs_hr_employee ON hs_hr_employee.emp_number = appraisal_list.emp_number  \n            LEFT JOIN ohrm_performance_appraisal_status AS status ON status.state_id =  appraisal_list.state_id       \n            WHERE $X{IN,appraisal_list.emp_number,empNumber} AND\n            hs_hr_employee.deleted_at IS NULL AND\n            $X{BETWEEN,appraisal_list.from_date,appraisal_period} AND\n            $X{BETWEEN,appraisal_list.to_date,appraisal_period} AND\n            $X{BETWEEN,appraisal_list.due_date,appraisal_due_period} AND\n            $X{IN,appraisal_list.state_id,appraisal_status}           \n            GROUP BY appraisal_list.id\n        </query>\n        <display_groups>\n            <display_group name=\"Appraisals\" type=\"one\" display=\"true\" show_header=\"true\">\n                <group_header>Appraisals</group_header>\n                <fields>\n                    <field display=\"false\">\n                        <field_name>appraisal_list.emp_number</field_name>\n                        <field_alias>empNumber</field_alias>\n                        <display_name>Emp Number</display_name>\n                        <width>100</width>\n                    </field>                    \n                    <field display=\"false\">\n                        <field_name>appraisal_list.id</field_name>\n                        <field_alias>appraisalId</field_alias>\n                        <display_name>appraisal_Id</display_name>\n                        <width>100</width>\n                        <format/>\n                        <align/>\n                    </field>\n                    <field display=\"true\">\n                        <field_name>appraisal_list.description</field_name>\n                        <field_alias>appraisalDescription</field_alias>\n                        <display_name>Description</display_name>\n                        <width>200</width>\n                        <format/>\n                        <align/>\n                    </field>\n                    <field display=\"true\">\n                        <field_name>CONCAT(UCASE(LEFT(status.name, 1)),LCASE(SUBSTRING(status.name, 2)))</field_name>\n                        <field_alias>appraisalStatus</field_alias>\n                        <display_name>Appraisal Status</display_name>\n                        <width>200</width>\n                        <format/>\n                        <align/>\n                   