(25, 'Performance Result Export', 'Performance_Result_Export', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<report>\n    <settings>\n        <csv>\n            <include_header>1</include_header>\n        </csv>\n        <data_group>Performance_Reports</data_group>\n        <export_history>1</export_history>\n    </settings>\n    <filter_fields>\n        <input_field type=\"text\" name=\"sub_unit\" label=\"Sub Unit\"></input_field>\n        <input_field type=\"text\" name=\"appraisal_from_date\" label=\"Appraisal From Date\" required=\"true\"></input_field>\n        <input_field type=\"text\" name=\"appraisal_to_date\" label=\"Appraisal To Date\" required=\"true\"></input_field>\n    </filter_fields>\n    <sub_report type=\"sql\" name=\"mainTable\">\n        <id_field>appraisalId</id_field>\n        <query>FROM ohrm_performance_appraisal\n               LEFT JOIN hs_hr_employee employee ON (employee.emp_number = ohrm_performance_appraisal.emp_number)\n               LEFT JOIN ohrm_performance_appraisal_evaluator ON (ohrm_performance_appraisal_evaluator.employee_appraisal_id = ohrm_performance_appraisal.id)\n               LEFT JOIN hs_hr_employee appraisalEvaluator ON (appraisalEvaluator.emp_number = ohrm_performance_appraisal_evaluator.employee_id)\n               LEFT JOIN ohrm_performance_evaluator_answer ON (ohrm_performance_evaluator_answer.employee_appraisal_evaluator_id = ohrm_performance_appraisal_evaluator.id)\n               LEFT JOIN ohrm_answer ON (ohrm_answer.id = ohrm_performance_evaluator_answer.answer_id)\n               LEFT JOIN ohrm_employee_salary_component ON (ohrm_employee_salary_component.employee_number = employee.emp_number)\n               LEFT JOIN ohrm_salary_record ON (ohrm_salary_record.employee_number = employee.emp_number)\n               LEFT JOIN hs_hr_currency_type ON (hs_hr_currency_type.currency_id = ohrm_salary_record.currency_id)\n               LEFT JOIN ohrm_question_form_question ON (ohrm_question_form_question.id = ohrm_ans