>\n        </input_field>\n        <input_field type=\"select\" name=\"course_title\" label=\"Course Title\" required=\"false\" allow-multiple=\"true\">\n            <options>\n                <choices type=\"sql\">\n                    <query>SELECT id, title FROM ohrm_training_course ORDER BY title</query>\n                    <value>id</value>\n                    <label>title</label>\n                </choices>\n            </options>\n        </input_field>\n        <input_field type=\"select\" name=\"course_version\" label=\"Course Version\" required=\"false\" allow-multiple=\"true\">\n            <options>\n                <choices type=\"sql\">\n                    <query>SELECT version FROM ohrm_training_course ORDER BY version</query>\n                    <value>version</value>\n                    <label>version</label>\n                </choices>\n            </options>\n        </input_field>\n        <input_field type=\"employee\" name=\"coordinators\" label=\"Course Coordinator\" allow-multiple=\"true\">\n            <options>\n                <search_mode>id</search_mode><!-- can be id or name -->\n            </options>\n        </input_field>\n        <input_field type=\"select\" name=\"course_company\" label=\"Course Company\" required=\"false\" allow-multiple=\"true\">\n            <options>\n                <choices type=\"sql\">\n                    <query>SELECT company FROM ohrm_training_course ORDER BY company</query>\n                    <value>company</value>\n                    <label>company</label>\n                </choices>\n            </options>\n        </input_field>\n        <input_field type=\"sub_unit\" name=\"sub_unit\" label=\"Sub Unit\" required=\"false\" allow-multiple=\"true\">\n            <options>\n                <choices type=\"sql\">\n                    <query>SELECT id, name, level FROM ohrm_subunit where id != 1 ORDER BY lft</query>\n                    <value>id</value>\n                    <label>name</label>\n