FT JOIN ohrm_training_session_student es ON e.emp_number = es.emp_number\n            LEFT JOIN ohrm_training_session_student_attendance sst ON sst.id = es.attendance\n            LEFT JOIN ohrm_training_session_student_rating ssr ON ssr.id = es.rating\n            LEFT JOIN ohrm_training_session s ON s.id = es.session_id\n            LEFT JOIN ohrm_training_session_delivery_method dm ON dm.id = s.delivery_method\n            LEFT JOIN ohrm_training_session_status ss ON ss.id = s.status\n            LEFT JOIN ohrm_user u ON u.id = s.created_user_id\n            LEFT JOIN ohrm_training_course c ON c.id = s.course_id\n            LEFT JOIN hs_hr_employee cc ON cc.emp_number = c.coordinator_id\n            LEFT JOIN ohrm_subunit su ON su.id = c.subunit_id\n            LEFT JOIN hs_hr_currency_type ccu ON ccu.currency_id  = c.currency_id\n            LEFT JOIN ohrm_training_course_status cs ON cs.id  = c.status\n            LEFT JOIN ohrm_user cu ON cu.id = c.created_user_id\n            LEFT JOIN ohrm_training_course_package cp ON cp.course_id = c.id\n            LEFT JOIN ohrm_training_package p ON p.id = cp.package_id\n            LEFT JOIN ohrm_training_package_status ps ON p.status = ps.id\n            LEFT JOIN ohrm_user pu ON pu.id = p.created_user_id\n            WHERE e.deleted_at IS NULL AND $X{IN,e.emp_number,emp_numbers} AND\n            $X{ACCESSIBLE_ENTITY_ID,e.emp_number,Employee} AND\n            $X{IN,sst.id,student_session_status}  AND\n            $X{FROM_WIDGET,es.attend_date,student_trained_date} AND\n            $X{IN,s.id,session_names}  AND\n            $X{FROM_WIDGET,s.scheduled_date,session_scheduled_date} AND\n            $X{FROM_WIDGET,s.due_date,session_due_date} AND\n            $X{IN,s.delivery_method,delivery_methods} AND\n            $X{IN,s.delivery_location,delivery_location} AND\n            $X{IN,s.status,session_status} AND\n            $X{IN,s.created_user_id,sesion_created_by} AND\n            $X{FROM_WIDGET,s.created_time,session