(31, 'Participant Progress Report', 'Employee_Progress_Report', '<?xml version=\"1.0\"?>\n<report>\n    <settings>\n        <csv>\n            <include_group_header>1</include_group_header>\n            <include_header>1</include_header>\n        </csv>\n        <data_group>onboarding_report_manipulation</data_group>\n    </settings>\n    <filter_fields> \n        <input_field type=\"employee\" name=\"emp_numbers\" label=\"Participant\" allow-multiple=\"true\">\n            <options>\n                <search_mode>id</search_mode><!-- can be id or name -->\n            </options>\n        </input_field>\n    </filter_fields> \n    <sub_report type=\"sql\" name=\"mainTable\">\n        <query>\n\n            FROM hs_hr_employee e\n            LEFT JOIN (\n                SELECT format( 100 * ( tsk.complete / tsk.total ) , 2 ) AS percentage, tsk.newHire AS newHire\n                FROM (\n                    SELECT tt.newHire, total, complete\n                    FROM (\n                          SELECT count( t.id ) AS total, t.new_hire_id AS newHire\n                          FROM ohrm_task t\n                          GROUP BY t.new_hire_id\n                         ) AS tt             \n                    LEFT JOIN (\n                          SELECT count( t.id ) AS complete, t.new_hire_id AS newHire\n                          FROM ohrm_task t\n                          WHERE t.status = \'COMPLETE\'\n                          GROUP BY t.new_hire_id\n                        )ct ON ct.newHire = tt.newHire\n                    ) AS tsk\n                ) AS emppro ON e.emp_number = emppro.newHire\n                WHERE $X{IN,e.emp_number,emp_numbers}\n                AND $X{ACCESSIBLE_ENTITY_ID,e.emp_number,Employee,true}\n                ORDER BY $Y{e.emp_firstname ASC}\n        </query>\n        <display_groups>\n            <display_group name=\"jobPercentageInformation\" type=\"one\" display=\"true\">\n                <group_header></group_header>\n              