INSERT INTO `ohrm_cron_task` (`id`, `name`, `short_description`, `long_description`, `command`, `observer_class`, `status`) VALUES
(1, 'Send Emails', 'This task will send all queued emails which are in pending status.', 'This task is executed on a daily basis on production systems. The frequency is set to once every minute or once every 15 minutes depending on the client\'s requirement.\n\nRunning this task via this screen task will send all queued emails which are in pending status.\n\nNote: This task will only send email notifications that are queued. As such, the \"Queue email notifications to be sent\" task must be executed prior to this.', 'php symfony orangehrm:send-email', 'EmailObserver', 1),
(2, 'Queue Email Notifications To Be Sent', 'Process notifications and add emails to the queue for sending.', 'This will process all enabled \"date based\" and \"scheduled\" email notifications in the system against the specified date or current date, create email notification accordingly and add them to the email queue.\nIf date is not specified current system date will be used.', 'php symfony orangehrm:ProcessNotification', 'ProcessNotificationObserver', 1),
(3, 'Fix Leave Balances: Single Leave Type', 'This task will correct leave balances by updating links between leave requests and entitlements.', 'This task is only executed when fixes are required to leave balances.\n\nRunning this task via this screen will correct leave balances by updating links between leave requests and entitlements.\n\nExamples/Possible scenarios:\n\n1. Fix leave balances of a given leave type (leave type id: 2) for the current leave period for all employees.\n[php symfony orangehrm:leave-balance-fix 2]\n\n2. Fix the leave balance of a given leave type (leave type id: 2) of a given employee (emp_number: 5) for the current leave period.\n[php symfony orangehrm:leave-balance-fix 2 5 ]\n\n3. Fix the leave balance of a given leave type (leave type id: 2) of a given employee (emp_number: 5) for the leave period applicable as at a given date (date: 2013-06-12).\n[php symfony orangehrm:leave-balance-fix 2 5 --date \"2013-06-12\"]\n\nIf the required date is not specified, the current date of the server will be considered.', 'php symfony orangehrm:leave-balance-fix', '', 1),
(4, 'Process Leave Accrual and Carry Forward', 'This process will process leave accrual and carry over calculations based on a given date.', 'This task is executed on a daily basis on production systems.It processes leave accrual and carry over calculations. The time of day will depend on the location/country and server time.\n\nRunning this task via this screen will process leave accrual and carry over calculations based on a given date.\nIf the required date is not specified, the current date of the server will be considered. Default time: 00:00:00.\n\nIf Operational Countries are enabled a drop down will appear to select the country so that the scheduled time will get appended.\n\nWarning: Please refrain from running this task more than once per day as it could duplicate carry over calculations.\n', 'php symfony LeaveAccrualAndCarryForward', 'LeaveAccrualObserver', 1),
(5, 'Fix Leave Balances: Bulk', 'Fix leave balances of all leave types for all leave periods.', 'Fix leave balances of all leave types for all leave periods (up to the current date of next year) for all employees.\n[php symfony orangehrm:leave-balance-fix --bulk]', 'php symfony orangehrm:leave-balance-fix --bulk', '', 1),
(6, 'Clear Email Queue', 'This will remove all the sent/pending/started emails from the email queue.', 'Once this process is executed, all the emails in the email queue will get deleted.', 'php symfony orangehrm:ClearEmailQueue', '', 1),
(7, 'Clear Leave Entitlements', 'Clear all leave Entitlements in the system.', 'This task will clear all leave Entitlements in the system for all employees and all leave types.\n\nIt is preferred to run Clear leave requests task before running this task. ', 'php symfony orangehrm:ClearLeaveEntitlements', '', 1),
(8, 'Clear Leave Requests', 'Clear all leave requests in the system.', 'This task will clear all leave requests in the system for all employees and all leave types.', 'php symfony orangehrm:ClearLeaveRequests', '', 1),
(9, 'Terminate Employment', 'This process Terminate Employees who has been marked for Terminate on given date.', 'This task is executed on a daily basis on production systems.It processes  Terminate Employees who has been marked for Terminate on given date', 'php symfony orangehrm:terminate-employment', '', 1);

