    DECLARE orangehrm_user_name VARCHAR(300) CHARSET UTF8;
    DECLARE statusName VARCHAR(100);
    DECLARE comment MEDIUMTEXT;
    IF (SELECT @no_review_history IS NULL) THEN 
        SET orangehrm_user_name=(SELECT IFNULL(CONCAT(hs_hr_employee.emp_firstname, ' ' , hs_hr_employee.emp_middle_name ,' ', hs_hr_employee.emp_lastname),ohrm_user.user_name) FROM ohrm_user LEFT JOIN  hs_hr_employee ON hs_hr_employee.emp_number = ohrm_user.emp_number WHERE ohrm_user.id = @orangehrm_user );
        SET orangehrm_user_name = IFNULL(orangehrm_user_name, 'admin');
        SET statusName='invalid';
        SET comment='';
        IF(NEW.comment_added_time != Old.comment_added_time or (Old.comment_added_time is null and NEW.comment_added_time is not null)) THEN
            SET comment = NEW.admin_comment;
        END IF;
        SET statusName = @workflow_action;
        IF(statusName != 'invalid') THEN
            INSERT INTO `ohrm_audittrail_performance_appraisal_trail`
            SET  
                 action_time=CURRENT_TIMESTAMP,
                 action_owner_id = @orangehrm_user,
                 action_owner_name= orangehrm_user_name,
                 version_id=1,
                 action=statusName,
                 affected_entity_id=NEW.id,
                 action_description=comment; 
        END IF;
    END IF;    
END
$$
DELIMITER ;

