hrm_membership.id)\n                    LEFT JOIN hs_hr_country ON \n                        (hs_hr_employee.coun_code = hs_hr_country.cou_code)                    \n                    LEFT JOIN hs_hr_emp_reportto AS supervisor_list ON \n                        (hs_hr_employee.emp_number = supervisor_list.erep_sub_emp_number) \n                    LEFT JOIN hs_hr_employee AS supervisor ON\n                        (supervisor.emp_number = supervisor_list.erep_sup_emp_number)\n                    LEFT JOIN ohrm_emp_reporting_method AS supervisor_reporting_method ON \n                        (supervisor_list.erep_reporting_mode = supervisor_reporting_method.reporting_method_id) \n                    LEFT JOIN ohrm_emp_termination ON\n                        (hs_hr_employee.termination_id = ohrm_emp_termination.id)\n                    LEFT JOIN ohrm_emp_termination_reason ON\n                        (ohrm_emp_termination.reason_id = ohrm_emp_termination_reason.id)\n                     LEFT JOIN ohrm_job_record ON\n                        (ohrm_job_record.employee_number = hs_hr_employee.emp_number)                     \n                     LEFT JOIN ohrm_direct_deposit_record ON\n                        (ohrm_direct_deposit_record.employee_number = hs_hr_employee.emp_number)\n                     LEFT JOIN ohrm_employee_salary_component ON\n                        (ohrm_employee_salary_component.employee_number = hs_hr_employee.emp_number AND ohrm_employee_salary_component.is_active = 1)\n                     LEFT JOIN ohrm_salary_component ON\n                        (ohrm_salary_component.id = ohrm_employee_salary_component.salary_component_id) \n                WHERE hs_hr_employee.emp_number in (\n                    SELECT hs_hr_employee.emp_number FROM hs_hr_employee                        \n                        LEFT JOIN ohrm_emp_education ON \n                            (hs_hr_employee.emp_number = ohrm_emp_education.emp_number) \n                        LEFT JOIN hs_hr_emp_skill ON \n                            (hs_hr_employee.emp_number = hs_hr_emp_skill.emp_number) \n                        LEFT JOIN hs_hr_emp_language ON \n                            (hs_hr_employee.emp_number = hs_hr_emp_language.emp_number) \n                    WHERE whereCondition1\n                )\n                \n                GROUP BY \n                     hs_hr_employee.emp_number,\n                     hs_hr_employee.emp_lastname,\n                     hs_hr_employee.emp_firstname,\n                     hs_hr_employee.emp_middle_name,\n                     hs_hr_employee.emp_birthday,\n                     ohrm_nationality.name,\n                     hs_hr_employee.emp_gender,\n                     hs_hr_employee.emp_marital_status,\n                     hs_hr_employee.emp_dri_lice_num,\n                     hs_hr_employee.emp_dri_lice_exp_date,\n                     hs_hr_employee.emp_street1,\n                     hs_hr_employee.emp_street2,\n                     hs_hr_employee.city_code,\n                     hs_hr_employee.provin_code,\n                     hs_hr_employee.emp_zipcode,\n                     hs_hr_country.cou_code,\n                     hs_hr_employee.emp_hm_telephone,\n                     hs_hr_employee.emp_mobile,\n                     hs_hr_employee.emp_work_telephone,\n                     hs_hr_employee.emp_work_email,\n                     hs_hr_employee.emp_oth_email\n\nORDER BY hs_hr_employee.emp_lastname'),
(4, 'vacancy_succession', 'SELECT vacancy_added_date, job_title, no_of_positions, no_of_applicants, SUM(no_of_shortlisted) AS no_of_shortlisted, SUM(no_of_hired) AS no_of_hired\nFROM\n(\n  SELECT\n  v.id,\n  v.job_title_code,\n  DATE(v.defined_time) AS `vacancy_added_date`,\n  jt.job_title AS `job_title`,\n  v.no_of_positions AS `no_of_positions`,\n  (\n    SELECT COUNT(*) FROM ohrm_job_candidate_vacancy AS jcv\n    WHERE jcv.vacancy_id = v.id\n  ) AS `no_of_applicants`,\n  MAX(IF(ve.type_id = 2 OR ve.type_id = 3 OR ve.type_id = 4 OR ve.type_id = 5 OR ve.type_id = 6, 1, 0)) AS `no_of_shortlisted`,\n  MAX(IF(type_id = 14, 1, 0)) AS `no_of_scheduled`,\n  MAX(IF(type_id = 15, 1, 0)) AS `no_of_passed`,\n  MAX(IF(type_id = 16, 1, 0)) AS `no_of_failed`,\n  MAX(IF(type_id = 7, 1, 0)) AS `no_of_hired`\n  FROM `ohrm_job_vacancy` v\n  INNER JOIN `ohrm_job_title` AS jt\n    ON v.job_title_code = jt.id\n  INNER JOIN `ohrm_job_candidate_vacancy` AS jcv\n    ON jcv.vacancy_id = v.id\n  INNER JOIN `ohrm_candidate_vacancy_event` AS cve\n    ON cve.vacancy_candidate_id = jcv.id\n  INNER JOIN `ohrm_vacancy_event` AS ve\n    ON ve.id = cve.event_id\n  INNER JOIN `ohrm_candidate_event_state` AS ces\n    ON ces.candidate_vacancy_event_id = cve.id\n  WHERE ces.active = 1\n  GROUP BY jcv.candidate_id\n  ) AS r\nWHERE whereCondition1\nGROUP BY r.id\nORDER BY r.vacancy_added_date DESC\n;'),
(5, 'employee_leave', 'SELECT  selectCondition\nFROM (\nSELECT CONVERT( CONCAT( date(le.from_date), \'to\', date(le.to_date) ) USING latin1 ) AS leave_period , lt.name AS `type` , ROUND(le.no_of_days,2) AS entitlement , \nSUM(IF(l.date between le.from_date and le.to_date, IF( l.status =3, ROUND(l.length_days,2), 0), 0)) AS taken_days,\nSUM(IF(l.date between le.from_date and le.to_date, IF(l.status = 2, ROUND(l.length_days,2), 0), 0)) AS scheduled_days,\nIF(let.name = \'Brought Forward\', ROUND(le.no_of_days,2),0) AS `brought_forward`,\n(IF(let.name = \'Added\', ROUND(le.no_of_days,2),0) + IF(let.name = \'Accrual\', ROUND(le.no_of_days,2),0) + IF(let.name = \'Brought Forward\', ROUND(le.no_of_days,2),0)) AS `total`\nFROM ohrm_leave l\nLEFT JOIN ohrm_leave_entitlement le ON l.emp_number = le.emp_number and le.leave_type_id = l.leave_type_id\nLEFT JOIN ohrm_leave_type lt ON lt.id = le.leave_type_id\nLEFT JOIN ohrm_leave_entitlement_type let ON let.id =   le.entitlement_type \nWHERE  whereCondition1\nGROUP BY lt.id,`leave_period`\nORDER BY `leave_period` DESC\n) AS `report_records`'),
(6, 'employee_turnover_hiring', 'SELECT selectCondition\nFROM `hs_hr_employee` AS e\nLEFT JOIN `ohrm_subunit` AS cs\n  ON e.work_station = cs.id\nLEFT JOIN `ohrm_job_title` AS jt\n  ON e.job_title_code = jt.id\nWHERE e.joined_date IS NOT NULL\n  AND whereCondition1\nGROUP BY e.emp_number\n;'),
(7, 'employee_turnover_termination', 'SELECT selectCondition\nFROM `hs_hr_employee` AS e\nLEFT JOIN `ohrm_subunit` AS cs\n  ON e.work_station = cs.id\nLEFT JOIN `ohrm_job_title` AS jt\n  ON e.job_title_code = jt.id\nLEFT JOIN `ohrm_emp_termination` AS te\n  ON e.termination_id = te.id\nLEFT JOIN `ohrm_emp_termination_reason` AS tr\n  ON te.reason_id = tr.id\nWHERE e.termination_id IS NOT NULL\n  AND whereCondition1\n;'),
(8, 'head_count', 'SELECT selectCondition\nFROM hs_hr_employee e\n\nLEFT JOIN ohrm_subunit s\nON whereCondition1 \n\nLEFT JOIN ohrm_subunit c\nON e.work_station = c.id\n\nLEFT JOIN ohrm_subunit p\nON p.level = s.level + 1 AND s.lft < p.lft and s.rgt > p.rgt AND p.lft <= c.lft and p.rgt >= c.rgt\n\nWHERE\n\ne.deleted_at IS NULL AND\n\nwhereCondition2\n\nAND\n\nwhereCondition3\n\nAND \n\n((s.id = 1)\nOR\n(p.id is not null or s.id = c.id))\n\nGROUP BY sub_unit\n;'),
